pragma solidity ^0.6;

import "./ERC20Burnable.sol";
import "./ERC20.sol";
import "./SafeMath.sol";
import "./fractional_nft_721_to_20.sol";

contract fnft_token is ERC20 {
    address _creator;//the fractional_nft_lock_contract
    address _token_address;
    uint256 _token_id;

    constructor(address creator,
                address token_address,
                uint256 token_id,
                uint256 whole_token_count,
                address receiver,
                string memory token_name,
                string memory token_ticker,
                uint8 decimals) public ERC20(token_name, token_ticker, decimals){
        _mint(receiver, whole_token_count);
        _creator = creator;
        _token_address = token_address;
        _token_id = token_id;
    }

    function close() public {
        require(balanceOf(msg.sender) == totalSupply());

        fractional_nft_721_to_20(_creator).release_NFT(_token_address, _token_id, msg.sender);//, to release NFT to msg.sender

        emit Closed(msg.sender);

        //kill contract
        selfdestruct(payable(_creator));
        //TODO what to do with any funds recovered from selfdestruct
    }

    event Closed(address user);
}

