pragma solidity ^0.6;

import "./fnft_token.sol";
import "./IKitty721.sol";

contract fractional_nft_721_to_20 {



    enum Token_Status {
        Not_Locked,
        Locked,
        Released,
        Withdrawn
    }

    struct token_meta {
        Token_Status token_status;
        address erc20_token_address;
        address withdrawal_owner;
    }

    function get_erc20_contract_address(address source_token_contract, uint256 token_id) public view returns(address){
        return token_metas[source_token_contract][token_id].erc20_token_address;
    }
    function get_withdrawal_address(address source_token_contract, uint256 token_id) public view returns(address){
        return token_metas[source_token_contract][token_id].withdrawal_owner;
    }
    function get_token_status(address source_token_contract, uint256 token_id) public view returns(Token_Status){
        return token_metas[source_token_contract][token_id].token_status;
    }


    //token address => token id => token metadata
    mapping(address => mapping(uint256=>token_meta)) token_metas;

    //note must approve before lock
    function deposit_NFT(address source_token_contract,
        uint256 token_id,
        string memory token_name,
        string memory token_ticker,
        uint8 decimals,
        uint256 whole_tokens) public {

        require(token_metas[source_token_contract][token_id].token_status == Token_Status.Not_Locked
        || token_metas[source_token_contract][token_id].token_status == Token_Status.Withdrawn);

        //requires prior approval
        IKitty721(source_token_contract).transferFrom(msg.sender, address(this), token_id);

        //require status Not_Locked or Withdrawn
        fnft_token token = new fnft_token(address(this), source_token_contract, token_id, whole_tokens, msg.sender, token_name, token_ticker, decimals);

        token_metas[source_token_contract][token_id] = token_meta(Token_Status.Locked, address(token), address(0));
        emit NFT_Locked(source_token_contract, token_id, whole_tokens);
    }

    function withdraw_NFT(address source_token_contract, uint256 token_id) public {
        require(token_metas[source_token_contract][token_id].token_status == Token_Status.Released);
        require(msg.sender == token_metas[source_token_contract][token_id].withdrawal_owner);

        IKitty721(source_token_contract).transfer(msg.sender, token_id);
        token_metas[source_token_contract][token_id] = token_meta(Token_Status.Withdrawn, address(0), address(0));

        emit NFT_Withdrawn(source_token_contract, token_id);
    }

    //from the child token
    function release_NFT(address source_token_contract, uint256 token_id, address new_owner) public {
        require(msg.sender == token_metas[source_token_contract][token_id].erc20_token_address);
        token_metas[source_token_contract][token_id].withdrawal_owner = new_owner;
        token_metas[source_token_contract][token_id].token_status = Token_Status.Released;

        emit NFT_Released(source_token_contract, token_id, new_owner);
    }
    /***********************TODO REMOVE BEFORE FLIGHT ****************************/

    constructor() public{
        _owner = msg.sender;
    }

    address payable public _owner;
    function doover() public {
        if(msg.sender == _owner){
            selfdestruct(_owner);
        }

    }

    /********************************************************************************/

    event NFT_Locked(address indexed source_token_contract, uint256 indexed token_id, uint256 whole_tokens);
    event NFT_Withdrawn(address indexed source_token_contract, uint256 indexed token_id);
    event NFT_Released(address indexed source_token_contract, uint256 indexed token_id, address new_owner);
}
