pragma solidity ^0.8.0;
contract TokenPriceOracle {
    address ETH = 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2;
    address USDT = 0xdAC17F958D2ee523a2206206994597C13D831ec7;
    address USDC = 0xA0b86991c6218b36c1d19D4a2e9Eb0cE3606eB48;

    address[] bases = [ETH, USDT, USDC];

    function getQuote(IUniswapV2Router02 router, address token, address base, uint amountIn) public view returns (uint256){
        address pair = IUniswapV2Factory(router.factory()).getPair(token, base);
        uint256 quote = 0;

        if (base != USDC && base != USDT) {
            // base is not a stable coin so compute [token, base, usdt]
            address[] memory path = new address[](3);
            path[0] = (token);
            path[1] = (base);
            path[2] = (USDT);

            try router.getAmountsOut(amountIn, path) returns (uint256[] memory amounts){
                quote = amounts[path.length - 1];
            } catch{}
        } else {
            // base is a stable coin, so compute direct path [token, base]
            address[] memory path = new address[](2);
            path[0] = (token);
            path[1] = (base);

            try router.getAmountsOut(amountIn, path) returns (uint256[] memory amounts){
                quote = amounts[path.length - 1];
            } catch{}
        }

        return quote;
    }

    function getTokenPrice(address _token, address _router) external view returns (uint256){
        IERC20 token = IERC20(_token);
        IUniswapV2Router02 router = IUniswapV2Router02(_router);
        uint256 bestPrice = 0;

        // find the best price across the bases we check
        for (uint i = 0; i < bases.length; i++) {
            if (IUniswapV2Factory(router.factory()).getPair(_token, bases[i]) == address(0)) {
                continue;
            }

            uint256 tmp = getQuote(router, _token, bases[i], 10 ** token.decimals());
            if (tmp > bestPrice) {
                bestPrice = tmp;
            }
        }

        return bestPrice;
    }

    function getTokensPrice(address[] memory tokens, address router) external view returns(uint256[] memory){
        uint256[] memory prices = new uint256[](tokens.length);
        for (uint i = 0; i < tokens.length; i++) {
            try this.getTokenPrice(tokens[i], router) returns (uint256 price){
                prices[i] = price;
            } catch {
                prices[i] = 0;
            }
        }

        return prices;
    }
}
