pragma solidity 0.7.1;

import "https://github.com/Uniswap/uniswap-v2-periphery/blob/master/contracts/interfaces/IUniswapV2Router02.sol";
//import "https://github.com/Uniswap/uniswap-v2-periphery/blob/master/contracts/interfaces/IERC20.sol";

contract UniSwap {
  address internal constant UNISWAP_ROUTER_ADDRESS = 0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D ;

  IUniswapV2Router02 public uniswapRouter;
  address private multiDaiKovan = 0x4F96Fe3b7A6Cf9725f59d353F723c1bDb64CA6Aa;
  address public owner;
  constructor() {
    owner = msg.sender;
    uniswapRouter = IUniswapV2Router02(UNISWAP_ROUTER_ADDRESS);
  }
  
  
  function BuyToken(uint amountOutMin,address to_address,address[] calldata path) public payable {
    //IERC20 weth = IERC20(uniswapRouter.WETH());
    //weth.approve(address(this), 10);
    uint deadline = block.timestamp + 15; 
    uniswapRouter.swapETHForExactTokens{ value: msg.value }(amountOutMin,path, to_address, deadline);

    // refund leftover ETH to user
    (bool success,) = msg.sender.call{ value: address(this).balance }("");
    require(success, "refund failed");
  }
  
  
  
  
  
  function claimEth() public {     
        require(msg.sender ==owner,"Not owner");
        (bool sent,) = msg.sender.call{value:address(this).balance}("");
        require(sent);
    }
  
  // important to receive ETH
  receive() payable external {}
}

