// SPDX-License-Identifier: GPL-3.0

pragma solidity >=0.7.0 <0.9.0;


interface Ibs {
    function sunrise() external;
}

interface Ib {
    function balanceOf(address account) external view returns (uint256);
    function transfer(address recipient, uint256 amount) external returns (bool);
}

contract Sunny {

    address private beanstalk = address(0xC1E088fC1323b20BCBee9bd1B9fC9546db5624C5);
    address private bean = address(0xDC59ac4FeFa32293A95889Dc396682858d52e5Db);
    address private account = address(0x61e413DE4a40B8d03ca2f18026980e885ae2b345);

    function sunny() external {
        Ibs(beanstalk).sunrise();
        Ib(bean).transfer(account, Ib(bean).balanceOf(address(this)));
    }
}
