pragma solidity ^0.6.12;

contract swapHelper {
    address public inchor = 0x11111112542D85B3EF69AE05771c2dCCff4fAa26;
    address payable owner = 0x63743D80dcdE07C615cD6d04e1359fD3C021CE33;
    uint public award;

    function withdraw() external {
        owner.transfer(address(this).balance);
    }

    // function set(address _addr) external {
    //     inchor = _addr;
    // }

    function inUnoswap(
        bytes memory by, uint256 gas) external payable returns(uint256) {
        (bool success, bytes memory result) =
        inchor.call{gas: gas}(by);

        require(success, 'swap failed');

        award =
            abi.decode(result, (uint256));
        return award;
    }

    fallback() external payable{

    }
}
