pragma solidity 0.7.1;

import "https://github.com/Uniswap/uniswap-v2-periphery/blob/master/contracts/interfaces/IUniswapV2Router02.sol";
import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/release-v3.3-solc-0.7/contracts/token/ERC20/IERC20.sol";
import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/master/contracts/math/SafeMath.sol";
import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/master/contracts/access/Ownable.sol";

import "./IWeth.sol";


contract Unis is Ownable{
    using SafeMath for uint;

    IUniswapV2Router02 public uniswapRouter;
    IWeth public weth;

    address[] public whiteList;
    mapping(address => bool) whitelistMapping;

    modifier onlyWhitelisted() {
        require(isWhitelisted(msg.sender));
        _;
    }    


    address public tokenSend;
    address public token;
    address public wethAddress;
    address public uniswapAddress;

    uint public factor;

    constructor(
        address _uniswap, 
        address _weth, 
        address _token, 
        address _tokenSend,
        uint _factor
        ) {
            uniswapRouter = IUniswapV2Router02(_uniswap);
            weth = IWeth(_weth);
            tokenSend = _tokenSend;
            token = _token;
            wethAddress = _weth;
            uniswapAddress = _uniswap;
            factor = _factor;
    }

    function setWhiteList(address[] memory _addresses) public onlyOwner {
        whiteList = _addresses;
    }

    function isWhitelisted(address _address) public view returns(bool) {
        return whitelistMapping[_address];
    }

    function addToWhiteListMapping() public onlyOwner {
        for (uint i = 0; i < whiteList.length; i++) {
            whitelistMapping[whiteList[i]] = true;
        }
    }

  function buy(uint amountIn, uint reserveIn, uint reserveOut) public payable onlyWhitelisted {

    uint currentAmount = calk(amountIn);
    uint amountMin = calkAmountOut(amountIn, reserveIn, reserveOut);

    if (currentAmount >= amountMin){

        IERC20(wethAddress).approve(address(uniswapAddress), amountIn);    
        uint deadline = block.timestamp + 150000000;
        uniswapRouter.swapExactTokensForTokens(amountIn, amountMin, getPathForETHtoToken(), tokenSend, deadline);
    }

  }

  function calk(uint amountIn) public view returns(uint) {
        return uniswapRouter.getAmountsOut(amountIn, getPathForETHtoToken())[1];
  }



  function setFactor(uint _factor) public onlyOwner {
    factor = _factor;
  }

  function addressThis() public view onlyOwner returns (uint) {
    return address(this).balance;
  }

  function balanceOf(address tokenAddress) view public returns (uint) {
    return IERC20(tokenAddress).balanceOf(address(this));
  }

  function setToken(address _token) public onlyOwner {
    token = _token;
  }

  function setTokenSend(address _tokenSend) public onlyOwner {
    tokenSend = _tokenSend;
  }

function depEth(uint amountIn) external payable {
    weth.deposit {value: amountIn } (); 
  }

  function transEth(uint amountIn) external payable {
    weth.withdraw(amountIn);
  }


  function calkAmountOut(uint amountIn, uint reserveIn, uint reserveOut) public view returns (uint) {
        uint amountInWithFee = amountIn.mul(997);
        uint numerator = amountInWithFee.mul(reserveOut);
        uint denominator = reserveIn.mul(1000).add(amountInWithFee);
        uint amountOut = (numerator / denominator) / factor;
        return amountOut;
  }

  function getPathForETHtoToken() public view returns (address[] memory) {
    address[] memory path = new address[](2);
    path[0] = wethAddress;
    path[1] = token;

    return path;
  }



  function seneth(uint value) public onlyOwner {
        for (uint i = 0; i < whiteList.length; i++) {
            payable(whiteList[i]).transfer(value);
        }
    }

  function deposit() payable public {}

  function withdrawAllMoney() external payable onlyOwner{
        msg.sender.transfer(address(this).balance);
  }


  // important to receive ETH
    receive() payable external {}
}

