interface IWrappedToken {
    function initialize(string calldata name, string calldata symbol, uint8 decimals, address owner) external;
    function mintTo(address recipient, uint256 amount) external returns (bool);
    function burn(uint256 amount) external returns (bool);
}

contract ERC20TokenImplementation is Ownable, IERC20, IERC20Metadata, IWrappedToken, Initializable {
    using SafeMath for uint256;

    string private name_;
    string private symbol_;
    uint8 private decimals_;
    uint256 private totalSupply_;
    mapping (address => uint256) private balances_;
    mapping (address => mapping (address => uint256)) private allowances_;
    
    function initialize(string calldata _name, string calldata _symbol, uint8 _decimals, address _owner) external virtual override initializer {
        require(_owner != address(0), "ERC20: owner cannot be zero address");
        
        initOwnable(_owner);
        
        name_ = _name;
        symbol_ = _symbol;
        decimals_ = _decimals;
    }

    function name() external view virtual override returns (string memory) {
        return name_;
    }

    function symbol() external view virtual override returns (string memory) {
        return symbol_;
    }

    function decimals() external view virtual override returns (uint8) {
        return decimals_;
    }

    function balanceOf(address _account) external view virtual override returns (uint256) {
        return balances_[_account];
    }

    function mint(uint256 _amount) public onlyOwner returns (bool) {
        _mint(_msgSender(), _amount);
        return true;
    }
    
    function mintTo(address _recipient, uint256 _amount) external onlyOwner virtual override returns (bool) {
        _mint(_recipient, _amount);
        return true;
    }

    function burn(uint256 _amount) external onlyOwner virtual override returns (bool) {
        _burn(_msgSender(), _amount);
        return true;
    }

    function totalSupply() external view virtual override returns (uint256) {
        return totalSupply_;
    }

    function transfer(address _recipient, uint256 _amount) external virtual override returns (bool) {
        _transfer(_msgSender(), _recipient, _amount);
        return true;
    }

    function allowance(address _owner, address _spender) external view virtual override returns (uint256) {
        return allowances_[_owner][_spender];
    }

    function increaseAllowance(address _spender, uint256 _addedValue) public returns (bool) {
        _approve(_msgSender(), _spender, allowances_[_msgSender()][_spender].add(_addedValue));
        return true;
    }

    function decreaseAllowance(address _spender, uint256 _subtractedValue) public returns (bool) {
        _approve(_msgSender(), _spender, allowances_[_msgSender()][_spender].sub(_subtractedValue, "ERC20: decreased allowance below zero"));
        return true;
    }

    function approve(address _spender, uint256 _amount) external virtual override returns (bool) {
        _approve(_msgSender(), _spender, _amount);
        return true;
    }

    function transferFrom(address _sender, address _recipient, uint256 _amount) external virtual override returns (bool) {
        _transfer(_sender, _recipient, _amount);

        uint256 currentAllowance = allowances_[_sender][_msgSender()];
        require(currentAllowance >= _amount, "ERC20: transfer amount exceeds allowance");
        unchecked {
            _approve(_sender, _msgSender(), currentAllowance - _amount);
        }

        return true;
    }

    function _mint(address _account, uint256 _amount) internal virtual {
        require(_account != address(0), "ERC20: mint to the zero address");

        _beforeTokenTransfer(address(0), _account, _amount);

        totalSupply_ += _amount;
        balances_[_account] += _amount;
        emit Transfer(address(0), _account, _amount);
    }

    function _burn(address _account, uint256 _amount) internal virtual {
        require(_account != address(0), "ERC20: burn from the zero address");

        _beforeTokenTransfer(_account, address(0), _amount);

        uint256 accountBalance = balances_[_account];
        require(accountBalance >= _amount, "ERC20: burn amount exceeds balance");
        unchecked {
            balances_[_account] = accountBalance - _amount;
            totalSupply_ -= _amount;
        }

        emit Transfer(_account, address(0), _amount);
    }

    function _transfer(address _sender, address _recipient, uint256 _amount) internal virtual {
        require(_sender != address(0), "ERC20: transfer from the zero address");
        require(_recipient != address(0), "ERC20: transfer to the zero address");

        _beforeTokenTransfer(_sender, _recipient, _amount);

        uint256 senderBalance = balances_[_sender];
        require(senderBalance >= _amount, "ERC20: transfer amount exceeds balance");
        unchecked {
            balances_[_sender] = senderBalance - _amount;
        }
        balances_[_recipient] += _amount;

        emit Transfer(_sender, _recipient, _amount);
    }

    function _approve(address _owner, address _spender, uint256 _amount) internal virtual {
        require(_owner != address(0), "ERC20: approve from the zero address");
        require(_spender != address(0), "ERC20: approve to the zero address");

        allowances_[_owner][_spender] = _amount;
        emit Approval(_owner, _spender, _amount);
    }

    function _beforeTokenTransfer(address _from, address _to, uint256 _amount) internal virtual { }
}
