contract TokenDistributor is AccessControlEnumerable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    bytes32 public constant CREATOR_ROLE = keccak256("CREATOR_ROLE");
    bytes32 public constant EDITOR_ROLE = keccak256("EDITOR_ROLE");
    bytes32 public constant WITHDRAWER_ROLE = keccak256("WITHDRAWER_ROLE");

    /// @notice Info of each distribution.
    /// `name` Unique string name.
    /// `distributionToken` Token to be distributed
    /// `totalRaised` Sum of all contributions.
    /// `totalDistributionTokenWithdrawn` Total cumulative amount of tokens withdrawn
    /// `paused` If true users cannot withdraw
    struct DistributionInfo {
        string name;
        address distributionToken;
        uint256 totalRaised;
        uint256 totalDistributionTokenWithdrawn;
        bool paused;
    }

    /// @notice Info of each user in a given distribution.
    /// `rewardWithdrawn` The amount of the distribution token withdrawn by the user
    /// `amountContributed` The amount of BUSD the user contributed to the deal
    struct UserInfo {
        uint256 rewardWithdrawn;
        uint256 amountContributed;
    }

    struct DepositInfo {
        uint256 amount;
        uint256 unlockTime;
    }


    /// @notice List of all distributions
    DistributionInfo[] public distributions;
    /// @notice List of contributors for each distribution
    address[][] public distributionContributors;
    /// @notice List of all the deposits for each distribution
    DepositInfo[][] public deposits;

    /// @notice Info of each user in distributions.
    /// @dev Mapping is distributionId -> user address -> user info.
    mapping (uint256 => mapping (address => UserInfo)) public userInfo;

    event Withdraw(address indexed user, uint256 indexed distributionId, uint256 amount);
    /// @dev Depositor does not have to be (and is usually not) a contributor
    event Deposit(address indexed depositor, uint256 indexed distributionId, uint256 amount);
    event LogCreateDistribution(
        address indexed creator,
        uint256 indexed distributionId,
        string indexed name,
        address distributionToken
    );
    event LogUpdateDistribution(
        uint256 indexed distributionId,
        bool paused,
        uint256 totalWithdrawn,
        uint256 totalRaised
    );
    event WithdrawIncorrectlySentFunds(address indexed tokenAddress, uint256 amount);

    constructor() {
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setupRole(CREATOR_ROLE, msg.sender);
        _setupRole(EDITOR_ROLE, msg.sender);
        _setupRole(WITHDRAWER_ROLE, msg.sender);
    }

    /// @notice Returns the number of distributions.
    function distributionLength() external view returns (uint256) {
        return distributions.length;
    }

    /// @notice Returns the contributors a distribution.
    function getContributors(uint256 _distributionId) external view returns (address[] memory) {
        return distributionContributors[_distributionId];
    }

    /// @notice Returns the deposit info for a distribution
    function getDeposits(uint256 _distributionId) external view returns (DepositInfo[] memory) {
        return deposits[_distributionId];
    }

    /// @notice Returns the distribution ids for all distributions the wallet is part of
    function getDistributionIdsForWallet(address _wallet) external view returns (uint256[] memory) {
        // we can't return dynamic arrays in view functions so first we have to calculate the size and then populate it
        uint256 resultCount;
        for (uint256 i = 0; i < distributionContributors.length; i++) {
            address[] memory aDistribution = distributionContributors[i];
            for (uint256 j = 0; j < aDistribution.length; j++) {
                if (aDistribution[j] == _wallet) {
                    resultCount++;
                }
            }
        }
        uint256[] memory result = new uint256[](resultCount);
				uint256 resultRunner = 0;
        for (uint256 i = 0; i < distributionContributors.length; i++) {
            address[] memory aDistribution = distributionContributors[i];
            for (uint256 j = 0; j < aDistribution.length; j++) {
                if (aDistribution[j] == _wallet) {
                    result[resultRunner] = i;
										resultRunner++;
                }
            }
        }
        return result;
    }

    function _totalUnlockedAsOfNow(uint256 _distributionId) private view returns (uint256) {
        DepositInfo[] storage allDeposits = deposits[_distributionId];
        uint256 total;
        for (uint256 i = 0; i < allDeposits.length; i++) {
            if (allDeposits[i].unlockTime <= block.timestamp) {
                total += allDeposits[i].amount;
            }
        }
        return total;
    }

    /// @notice Returns the total amount deposited for a distribution including future unlocks
    function totalDeposited(uint256 _distributionId) public view returns (uint256) {
    DepositInfo[] storage allDeposits = deposits[_distributionId];
    uint256 total;
    for (uint256 i = 0; i < allDeposits.length; i++) {
        total += allDeposits[i].amount;
    }
    return total;
}

    /// @notice View function to see the pending rewards for a given user on the
    /// frontend.
    function pendingReward(uint256 _distributionId, address _user) public view returns (int256) {
        DistributionInfo memory distribution = distributions[_distributionId];
        UserInfo storage user = userInfo[_distributionId][_user];

        uint256 effectiveTotal = _totalUnlockedAsOfNow(_distributionId);
        uint256 expectedTotalPayment = (effectiveTotal * user.amountContributed) / distribution.totalRaised;

        int256 pendingReward = int256(expectedTotalPayment) - int256(user.rewardWithdrawn);
        uint256 tokensRemaining = effectiveTotal - distribution.totalDistributionTokenWithdrawn;

        return int256(tokensRemaining) > pendingReward ? pendingReward : int256(tokensRemaining);
    }

    /// @notice Creates a new distribution.
    /// @dev Amount at amounts index i should be the contribution for
    /// contributor at contributors index i.
    /// Names must be unique.
    /// contributor addresses MUST be unique
    function create(
        string calldata name,
        address distributionToken,
        address[] calldata contributors,
        uint256[] calldata amounts
    ) external onlyRole(CREATOR_ROLE) {
        for (uint256 i = 0; i < distributions.length; i++) {
            require(
                keccak256(abi.encodePacked(distributions[i].name)) != keccak256(abi.encodePacked(name)),
                'a distribution with that name already exists'
            );
        }

        DistributionInfo memory distribution = DistributionInfo(name, distributionToken, 0, 0, false);
        distributions.push(distribution);
        distributionContributors.push();
        deposits.push();

        emit LogCreateDistribution(
            msg.sender,
            distributions.length - 1,
            distribution.name,
            distribution.distributionToken
        );

        addContributors(distributions.length - 1, contributors, amounts);
    }

    /// @notice Adds contributors to a distribution.
    /// @dev Used because the number of contributors can often exceed the block gas limit
    function addContributors(uint256 distributionId, address[] calldata contributors, uint256[] calldata amounts) public onlyRole(CREATOR_ROLE) {
        require(contributors.length > 0, 'must have contributors');
        require(contributors.length == amounts.length, 'contributors and amounts must be same length');
        require(contributors.length <= 200, 'cannot process more than 200 at once');

        address[] storage existingContributors = distributionContributors[distributionId];
        uint256 total;
        for (uint256 i = 0; i < amounts.length; i++) {
            require(amounts[i] > 0, "cannot contribute zero amount");
            UserInfo storage user = userInfo[distributionId][contributors[i]];
            require(user.amountContributed == 0, "duplicate contributor not allowed");
            total = total + amounts[i];
            user.amountContributed = amounts[i];

            existingContributors.push(contributors[i]);
        }

        DistributionInfo storage distribution = distributions[distributionId];
        distribution.totalRaised = distribution.totalRaised + total;


        emit LogUpdateDistribution(
            distributionId,
            distribution.paused,
            distribution.totalDistributionTokenWithdrawn,
            distribution.totalRaised
        );
    }

    /// @notice Deposit tokens to be distributed.
    /// @param _unlockTime is the time at which this deposit will be unlocked. For safety reasons this must be in the future or zero.
    /// Zero denotes an immediate unlock.
    function deposit(uint256 _distributionId, uint256 _amount, uint256 _unlockTime) external nonReentrant {
        require(_unlockTime > block.timestamp || _unlockTime == 0, "unlock time must be in the future or zero");
        DistributionInfo storage distribution = distributions[_distributionId];

        DepositInfo memory aDeposit = DepositInfo(_amount, _unlockTime);
        deposits[_distributionId].push(aDeposit);

        IERC20(distribution.distributionToken).safeTransferFrom(msg.sender, address(this), _amount);

        emit Deposit(msg.sender, _distributionId, _amount);
    }

    /// @notice Withdraw tokens from a distribution.
    function withdraw(uint256 _distributionId, uint256 _amount) external nonReentrant {
        DistributionInfo storage distribution = distributions[_distributionId];
        UserInfo storage user = userInfo[_distributionId][msg.sender];

        require(!distribution.paused, "withdrawals temporarily paused");

        int256 withdrawableAmount = pendingReward(_distributionId, msg.sender);
        require(int256(_amount) <= withdrawableAmount, 'amount exceeds withdrawable amount');

        user.rewardWithdrawn = user.rewardWithdrawn + _amount;
        distribution.totalDistributionTokenWithdrawn = distribution.totalDistributionTokenWithdrawn + _amount;
        IERC20(distribution.distributionToken).safeTransfer(msg.sender, _amount);

        emit Withdraw(msg.sender, _distributionId, _amount);
        emit LogUpdateDistribution(
            _distributionId,
            distribution.paused,
            distribution.totalDistributionTokenWithdrawn,
            distribution.totalRaised
        );
    }

    /// @notice Change the paused state of a distribution. Whilst a distribution
    /// is paused tokens cannot be withdrawn by contributors but still accrue
    /// normally.
    function setPaused(uint256 _distributionId, bool _paused) external onlyRole(EDITOR_ROLE) {
        DistributionInfo storage distribution = distributions[_distributionId];
        distribution.paused = _paused;

        emit LogUpdateDistribution(
            _distributionId,
            distribution.paused,
            distribution.totalDistributionTokenWithdrawn,
            distribution.totalRaised
        );
    }

    function _undoFutureDeposit(uint256 _distributionId, uint256 _depositIndex) private onlyRole(WITHDRAWER_ROLE) {
        DepositInfo storage aDeposit = deposits[_distributionId][_depositIndex];
        DistributionInfo storage distribution = distributions[_distributionId];
        require(aDeposit.unlockTime > block.timestamp, "deposit must be in the future");

        uint256 withdrawAmount = aDeposit.amount;
        aDeposit.amount = 0;

        IERC20(distribution.distributionToken).safeTransfer(msg.sender, withdrawAmount);
    }

    /// @notice Attempt to refund the entire amount of a deposit to the caller. Only to be used in emergencies.
    /// Future deposits can be rolled back without a problem. Ones in the past are on a best-effort basis. If users
    /// have already withdrawn from them then it will only remove what it can.
    /// @dev User pending rewards may go negative after this if the amount they've withdrawn is more than they'd be allocated
    /// given the total deposits after withdrawal.
    function undoDeposit(uint256 _distributionId, uint256 _depositIndex) external nonReentrant onlyRole(WITHDRAWER_ROLE) {
        DepositInfo storage aDeposit = deposits[_distributionId][_depositIndex];
        if (aDeposit.unlockTime > block.timestamp) {
            return _undoFutureDeposit(_distributionId, _depositIndex);
        }

        DistributionInfo storage distribution = distributions[_distributionId];
        uint256 totalUnlockedAsOfNow = _totalUnlockedAsOfNow(_distributionId);
        uint256 totalRemainingAsOfNow = totalUnlockedAsOfNow - distribution.totalDistributionTokenWithdrawn;

        if (totalRemainingAsOfNow >= aDeposit.amount) {
            uint256 withdrawAmount = aDeposit.amount;
            aDeposit.amount = 0;
            IERC20(distribution.distributionToken).safeTransfer(msg.sender, withdrawAmount);
            return;
        }

        uint256 withdrawAmount = totalRemainingAsOfNow;
        aDeposit.amount -= totalRemainingAsOfNow;
        IERC20(distribution.distributionToken).safeTransfer(msg.sender, withdrawAmount);
    }


    /// @notice Withdraws excess ERC20 tokens sent to the address.
    /// @dev Only counts tokens NOT sent via the deposit method.
    function withdrawIncorrectlySentFunds(address _tokenAddress, uint256 _amount) external nonReentrant onlyRole(WITHDRAWER_ROLE) {
        uint256 totalDepositedAmount;
        for (uint i = 0; i < distributions.length; i++) {
            if (distributions[i].distributionToken == _tokenAddress) {
                uint256 remainingForWithdrawal = totalDeposited(i) - distributions[i].totalDistributionTokenWithdrawn;
                totalDepositedAmount = totalDepositedAmount + remainingForWithdrawal;
            }
        }
        uint256 excessFunds = IERC20(_tokenAddress).balanceOf(address(this)) - totalDepositedAmount;
        require(_amount <= excessFunds, "amount exceeds excess funds");

        IERC20(_tokenAddress).safeTransfer(msg.sender, _amount);

        emit WithdrawIncorrectlySentFunds(_tokenAddress, _amount);
    }
}
