pragma solidity 0.6.6;

import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/master/contracts/token/ERC20/SafeERC20.sol";
import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/master/contracts/access/Ownable.sol";
import "https://github.com/Uniswap/uniswap-v2-core/blob/master/contracts/interfaces/IUniswapV2Callee.sol";
import "https://github.com/Uniswap/uniswap-v2-core/blob/master/contracts/interfaces/IUniswapV2Pair.sol";
import "https://github.com/Uniswap/uniswap-v2-core/blob/master/contracts/interfaces/IUniswapV2Factory.sol";
import "./UniswapV2Library1.sol";

interface Boardroom {
    function stake(uint256) external;
    function exit() external;
}

interface Treasury {
    function allocateSeigniorage() external;
}

contract BasisContractProd is IUniswapV2Callee, Ownable {
    using SafeERC20 for IERC20;

    address private dai = address(0x6B175474E89094C44Da98b954EedeAC495271d0F);
    address private bas = address(0xa7ED29B253D8B4E3109ce07c80fc570f81B63696);

    address constant private boardroomAddress = address(0x4B182469337d46E6603ed7e26BA60c56930a342c);
    address constant private treasuryAddress = address(0x67a883D6f84A2D307D8F587B638e2f172Ef2117A);
    address constant private uniswapFactoryAddress = address(0x5C69bEe701ef814a2B6a3EDD4B1652CB9cc5aA6f); // universal

    constructor() public {}

    function startWithAllReserves() onlyOwner public {
        IUniswapV2Pair daiBasPair = IUniswapV2Pair(UniswapV2Library.pairFor(uniswapFactoryAddress, dai, bas));
        (uint112 reserve0, uint112 reserve1, uint32 blockTimestampLast) = daiBasPair.getReserves();
        daiBasPair.swap(0,reserve1,address(this), bytes("1"));
    }

    function start(uint256 _amount) onlyOwner public {
        IUniswapV2Pair daiBasPair = IUniswapV2Pair(UniswapV2Library.pairFor(uniswapFactoryAddress, dai, bas));
        daiBasPair.swap(0,_amount,address(this), bytes("1"));
    }

    function uniswapV2Call(address sender, uint amount0, uint amount1, bytes calldata data) external override {
        address token0 = IUniswapV2Pair(msg.sender).token0();
        address token1 = IUniswapV2Pair(msg.sender).token1();
        assert(msg.sender == UniswapV2Library.pairFor(uniswapFactoryAddress, token0, token1)); // ensure that msg.sender is actually a V2 pair
        require(amount0 == 0, "wrong"); // this strategy is unidirectional
        uint amountBas = token1 == bas ? amount1 : amount0;

        IERC20 basToken = IERC20(bas);
        basToken.safeApprove(boardroomAddress, 0);
        basToken.safeApprove(boardroomAddress, amountBas);
        Boardroom(boardroomAddress).stake(amountBas);
        Treasury(treasuryAddress).allocateSeigniorage();
        Boardroom(boardroomAddress).exit();

        // compute amount of tokens that need to be paid back
        uint fee = ((amountBas * 3) / 997) + 1;
        uint amountToRepay = amountBas + fee;
        basToken.safeTransfer(msg.sender, amountToRepay);
    }

    function withdrawErc20(address erc20Address) onlyOwner public {
      IERC20(erc20Address).transfer(msg.sender, IERC20(erc20Address).balanceOf(address(this)));
    }
}

