contract Ownable is Context {
    address private _owner;

    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    /**
     * @dev Initializes the contract setting the deployer as the initial owner.
     */
    constructor () internal {
        _owner = _msgSender();
        emit OwnershipTransferred(address(0), _owner);
    }

    /**
     * @dev Returns the address of the current owner.
     */
    function owner() public view returns (address) {
        return _owner;
    }

    /**
     * @dev Throws if called by any account other than the owner.
     */
    modifier onlyOwner() {
        require(isOwner(), "Ownable: caller is not the owner");
        _;
    }

    /**
     * @dev Returns true if the caller is the current owner.
     */
    function isOwner() public view returns (bool) {
        return _msgSender() == _owner;
    }

    /**
     * @dev Leaves the contract without owner. It will not be possible to call
     * `onlyOwner` functions anymore. Can only be called by the current owner.
     *
     * NOTE: Renouncing ownership will leave the contract without an owner,
     * thereby removing any functionality that is only available to the owner.
     */
    function renounceOwnership() public onlyOwner {
        emit OwnershipTransferred(_owner, address(0));
        _owner = address(0);
    }

    /**
     * @dev Transfers ownership of the contract to a new account (`newOwner`).
     * Can only be called by the current owner.
     */
    function transferOwnership(address newOwner) public onlyOwner {
        _transferOwnership(newOwner);
    }

    /**
     * @dev Transfers ownership of the contract to a new account (`newOwner`).
     */
    function _transferOwnership(address newOwner) internal {
        require(newOwner != address(0), "Ownable: new owner is the zero address");
        emit OwnershipTransferred(_owner, newOwner);
        _owner = newOwner;
    }
}

interface IERC20 {
    function totalSupply() external view returns (uint256);
    function balanceOf(address account) external view returns (uint256);
    function transfer(address recipient, uint256 amount) external returns (bool);
    function allowance(address owner, address spender) external view returns (uint256);
    function approve(address spender, uint256 amount) external returns (bool);
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);
    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);
}

library SafeMath {
    function add(uint256 a, uint256 b) internal pure returns (uint256) {
        uint256 c = a + b;
        require(c >= a, "SafeMath: addition overflow");

        return c;
    }
    function sub(uint256 a, uint256 b) internal pure returns (uint256) {
        return sub(a, b, "SafeMath: subtraction overflow");
    }
    function sub(uint256 a, uint256 b, string memory errorMessage) internal pure returns (uint256) {
        require(b <= a, errorMessage);
        uint256 c = a - b;

        return c;
    }
    function mul(uint256 a, uint256 b) internal pure returns (uint256) {
        if (a == 0) {
            return 0;
        }

        uint256 c = a * b;
        require(c / a == b, "SafeMath: multiplication overflow");

        return c;
    }
    function div(uint256 a, uint256 b) internal pure returns (uint256) {
        return div(a, b, "SafeMath: division by zero");
    }
    function div(uint256 a, uint256 b, string memory errorMessage) internal pure returns (uint256) {
        // Solidity only automatically asserts when dividing by 0
        require(b > 0, errorMessage);
        uint256 c = a / b;

        return c;
    }
    function mod(uint256 a, uint256 b) internal pure returns (uint256) {
        return mod(a, b, "SafeMath: modulo by zero");
    }
    function mod(uint256 a, uint256 b, string memory errorMessage) internal pure returns (uint256) {
        require(b != 0, errorMessage);
        return a % b;
    }
}

library Address {
    function isContract(address account) internal view returns (bool) {
        bytes32 codehash;
        bytes32 accountHash = 0xc5d2460186f7233c927e7db2dcc703c0e500b653ca82273b7bfad8045d85a470;
        // solhint-disable-next-line no-inline-assembly
        assembly { codehash := extcodehash(account) }
        return (codehash != 0x0 && codehash != accountHash);
    }
    function toPayable(address account) internal pure returns (address payable) {
        return address(uint160(account));
    }
    function sendValue(address payable recipient, uint256 amount) internal {
        require(address(this).balance >= amount, "Address: insufficient balance");

        // solhint-disable-next-line avoid-call-value
        //(bool success, ) = recipient.call.value(amount)("");
        (bool success, ) = recipient.call{value:amount}(""); // todo : for 0.6.0. need to check

        require(success, "Address: unable to send value, recipient may have reverted");
    }
}

library SafeERC20 {
    using SafeMath for uint256;
    using Address for address;

    function safeTransfer(IERC20 token, address to, uint256 value) internal {
        callOptionalReturn(token, abi.encodeWithSelector(token.transfer.selector, to, value));
    }

    function safeTransferFrom(IERC20 token, address from, address to, uint256 value) internal {
        callOptionalReturn(token, abi.encodeWithSelector(token.transferFrom.selector, from, to, value));
    }

    function safeApprove(IERC20 token, address spender, uint256 value) internal {
        require((value == 0) || (token.allowance(address(this), spender) == 0),
            "SafeERC20: approve from non-zero to non-zero allowance"
        );
        callOptionalReturn(token, abi.encodeWithSelector(token.approve.selector, spender, value));
    }

    function safeIncreaseAllowance(IERC20 token, address spender, uint256 value) internal {
        uint256 newAllowance = token.allowance(address(this), spender).add(value);
        callOptionalReturn(token, abi.encodeWithSelector(token.approve.selector, spender, newAllowance));
    }

    function safeDecreaseAllowance(IERC20 token, address spender, uint256 value) internal {
        uint256 newAllowance = token.allowance(address(this), spender).sub(value, "SafeERC20: decreased allowance below zero");
        callOptionalReturn(token, abi.encodeWithSelector(token.approve.selector, spender, newAllowance));
    }
    function callOptionalReturn(IERC20 token, bytes memory data) private {
        require(address(token).isContract(), "SafeERC20: call to non-contract");

        // solhint-disable-next-line avoid-low-level-calls
        (bool success, bytes memory returndata) = address(token).call(data);
        require(success, "SafeERC20: low-level call failed");

        if (returndata.length > 0) { // Return data is optional
            // solhint-disable-next-line max-line-length
            require(abi.decode(returndata, (bool)), "SafeERC20: ERC20 operation did not succeed");
        }
    }
}

contract Storage is WhitelistedRole, Ownable{
    using SafeERC20 for IERC20;
    using SafeMath for uint256;		

    mapping (address => mapping (address => uint256)) private TokenBalance;
    mapping (address => mapping (address => uint256)) private USDUnitPrice;
    mapping (address => address []) private UserInvestList;
    
    event Log(address _target);

    receive() payable external {}

    ///////////////////////////////////////////////////////////////////////////////////////////////////////////
    function Send (address _token, address _target,uint256 _amt) external {
        require(isWhitelisted(msg.sender), "WhitelistedRole: caller does not have the Whitelisted role");
        require(TokenBalance[_target][_token]  >= _amt, "Storage: The number of LP Token is insufficent");
        TokenBalance[_target][_token] = TokenBalance[_target][_token].sub(_amt);
        if(TokenBalance[_target][_token] == 0){
            for (uint256 pid = 0; pid < UserInvestList[_target].length; ++pid) {
                if(UserInvestList[_target][pid] == _token){
                    delete UserInvestList[_target][pid];
                    break;
                }
            }
        }
        IERC20(_token).safeTransfer(msg.sender, _amt);
    }
    ///////////////////////////////////////////////////////////////////////////////////////////////////////////
    function GetInvestList(address user) external view returns(address [] memory){
        return UserInvestList[user];
    }
    
    function GetUSDUnitPrice(address _target, address _pair) external view returns (uint256){
        //require(isWhitelisted(msg.sender), "WhitelistedRole: caller does not have the Whitelisted role");
        return USDUnitPrice[_target][_pair];
    }
    
    function GetTokenBalance(address _target, address _pair) external view returns (uint256){
        //require(isWhitelisted(msg.sender), "WhitelistedRole: caller does not have the Whitelisted role");
        return TokenBalance[_target][_pair];
    }

    ///////////////////////////////////////////////////////////////////////////////////////////////////////////
    function UpdateUSDUnitPrice(address _target, address _pair, uint256 _amt, uint256 _amtUSD) external {
        require(isWhitelisted(msg.sender), "WhitelistedRole: caller does not have the Whitelisted role");
        USDUnitPrice[_target][_pair] = ((TokenBalance[_target][_pair].mul(USDUnitPrice[_target][_pair])).add(_amtUSD)).div(TokenBalance[_target][_pair].add(_amt));
    }
    function AddUSDUnitPrice(address _target, address _pair, uint256 _amt) external {
        require(isWhitelisted(msg.sender), "WhitelistedRole: caller does not have the Whitelisted role");
        USDUnitPrice[_target][_pair] = USDUnitPrice[_target][_pair] .add(_amt);
    }
    function SubUSDUnitPrice(address _target, address _pair, uint256 _amt) external {
        require(isWhitelisted(msg.sender), "WhitelistedRole: caller does not have the Whitelisted role");
        USDUnitPrice[_target][_pair] = USDUnitPrice[_target][_pair] .sub(_amt);
    }
    function SetTokenBalance(address _target, address _pair, uint256 _new_balance) external {
        require(isWhitelisted(msg.sender), "WhitelistedRole: caller does not have the Whitelisted role");
        TokenBalance[_target][_pair] = _new_balance;
    }
    function AddTokenBalance(address _target, address _pair, uint256 _amt) external {
        require(isWhitelisted(msg.sender), "WhitelistedRole: caller does not have the Whitelisted role");
        TokenBalance[_target][_pair] = TokenBalance[_target][_pair].add(_amt);
        bool isNew = false;
        for (uint256 pid = 0; pid < UserInvestList[_target].length; ++pid) {
            if(UserInvestList[_target][pid] == _pair){
                isNew = true;
                break;
            }
        }
        if(isNew==false){
            UserInvestList[_target].push(_pair);
        }

    }

    ///////////////////////////////////////////////////////////////////////////////////////////////////////////
    function selfDesturctor() onlyOwner public {
        selfdestruct(msg.sender); 
    }
    function emergencyTokenWithdrawal(address _token, uint256 _amount) onlyOwner public {
        IERC20(_token).safeTransfer(msg.sender, _amount);
    }
    function emergencyETHWithdrawal() onlyOwner public{
        payable(msg.sender).transfer(payable(address(this)).balance);
    }
}
