// ----------------------------------------------------------------------------
// Owned contract
// ----------------------------------------------------------------------------
contract Owned {
    address payable public owner;

    event OwnershipTransferred(address indexed _from, address indexed _to);

    constructor() public {
        owner = msg.sender;
    }

    modifier onlyOwner {
        require(msg.sender == owner);
        _;
    }

    function transferOwnership(address payable _newOwner) public onlyOwner {
        owner = _newOwner;
        emit OwnershipTransferred(msg.sender, _newOwner);
    }
}

contract Presale is Owned{
    using SafeMath for uint256;
    uint256 tokenRatePerEth = 350; // 1 eth = 350 dmon
    uint256 public ethRaised;
    address degen;
    
    // ------------------------------------------------------------------------
    // Constructor
    // ------------------------------------------------------------------------
    constructor(address _degen) public {
        owner = 0xb0aB6f008cE4A62bf731542826627663a57B9a23;
        degen = _degen;
    }
    
    receive() external payable{
        buy();
    }

    function buy() public payable{
        uint tokens = getTokenAmount(msg.value);
        
        require(IERC20(degen).transfer(msg.sender, tokens), "Insufficient balance of sale contract!");
        ethRaised = ethRaised.add(msg.value);
        owner.transfer(msg.value);
    }

    function getTokenAmount(uint256 amount) view internal returns(uint256){
        return amount.mul(tokenRatePerEth);
    }
    
    function getUnSoldTokens() external onlyOwner{
        uint256 tokens = IERC20(degen).balanceOf(address(this));
        require(IERC20(degen).transfer(owner, tokens), "No tokens in contract");
    }
}
