contract ToksiToken is Ownable, IERC20 {
    using SafeMath for uint256;
    string private _name = "Toksi";
    string private _symbol = "TOI";
    uint8 private _decimals = 18;
    uint256 private _totalSupply;

    mapping(address => uint256) private _balances;
    mapping(address => mapping(address => uint256)) private _allowances;

    /**
     * @dev Sets the values for {totalSupply} and transafer those created tokens to project creators.
     *
     * This value is immutable: it can only be set once during
     * construction.
     */

    constructor(
        uint256 _initialSupply,
        address _ReserveWallet,
        address _PresaleWallet,
        address _FounderWallet,
        address _ecosystemWallet,
        address _MarketingWallet
    ) public {
        _totalSupply = _initialSupply;
        _balances[_ReserveWallet] = _initialSupply.mul(128).div(1000);
        _balances[_PresaleWallet] = _initialSupply.mul(12).div(1000);
        _balances[_FounderWallet] = _initialSupply.mul(5).div(100);
        _balances[_ecosystemWallet] = _initialSupply.mul(10).div(100);
        _balances[_MarketingWallet] = _initialSupply.mul(20).div(100);
        _balances[msg.sender] = _initialSupply.mul(51).div(100);
    }

    /**
     * @dev Returns the name of the token.
     */
    function name() public virtual view returns (string memory) {
        return _name;
    }

    /**
     * @dev Returns the symbol of the token, usually a shorter version of the
     * name.
     */
    function symbol() public virtual view returns (string memory) {
        return _symbol;
    }

    function decimals() public virtual view returns (uint8) {
        return _decimals;
    }

    /**
     * @dev See {IERC20-totalSupply}.
     */
    function totalSupply() public virtual override view returns (uint256) {
        return _totalSupply;
    }

    /**
     * @dev See {IERC20-balanceOf}.
     */
    function balanceOf(address account)
        public
        virtual
        override
        view
        returns (uint256)
    {
        return _balances[account];
    }

    /**
     * @dev See {IERC20-transfer}.
     *
     * Requirements:
     *
     * - `recipient` cannot be the zero address.
     * - the caller must have a balance of at least `amount`.
     */
    function transfer(address recipient, uint256 amount)
        public
        virtual
        override
        returns (bool)
    {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    /**
     * @dev See {IERC20-allowance}.
     */
    function allowance(address owner, address spender)
        public
        virtual
        override
        view
        returns (uint256)
    {
        return _allowances[owner][spender];
    }

