pragma solidity ^0.7.6;






//import "hardhat/console.sol";

contract Arb is Ownable, DSMath {

    using SafeERC20 for IERC20;

    uint private constant MAX_INT = 2**256 - 1;
    IDeFiPlaza private constant plaza = IDeFiPlaza(0x134fc6bEB54e710E30b21e52Fb86fFED0C8e36C2);
    IUniswapV2Router02 private constant router = IUniswapV2Router02(0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D);
    IUniswapV2Factory private constant factory = IUniswapV2Factory(0x5C69bEe701ef814a2B6a3EDD4B1652CB9cc5aA6f);
    address private constant weth = 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2;
    uint public tradeAmount = 5e17; // 0.5 WETH

    constructor() {
        // approve all token pairs
        IERC20(weth).safeApprove(address(router), MAX_INT);
        IERC20(0x6468e79A80C0eaB0F9A2B574c8d5bC374Af59414).safeApprove(address(router), MAX_INT); // exrd
        IERC20(0x1f9840a85d5aF5bf1D1762F925BDADdC4201F984).safeApprove(address(router), MAX_INT); // uni
        IERC20(0x2260FAC5E5542a773Aa44fBCfeDf7C193bc2C599).safeApprove(address(router), MAX_INT); // wbtc
        IERC20(0x7D1AfA7B718fb893dB30A3aBc0Cfc608AaCfeBB0).safeApprove(address(router), MAX_INT); // matic
        IERC20(0x95aD61b0a150d79219dCF64E1E6Cc01f0B64C4cE).safeApprove(address(router), MAX_INT); // shib
        IERC20(0x7Fc66500c84A76Ad7e9c93437bFc5Ac33E2DDaE9).safeApprove(address(router), MAX_INT); // aave
        IERC20(0x0bc529c00C6401aEF6D220BE8C6Ea1667F6Ad93e).safeApprove(address(router), MAX_INT); // yfi
        IERC20(0x9f8F72aA9304c8B593d555F12eF6589cC3A579A2).safeApprove(address(router), MAX_INT); // mkr
        IERC20(0x6B3595068778DD592e39A122f4f5a5cF09C90fE2).safeApprove(address(router), MAX_INT); // sushi
        IERC20(0x514910771AF9Ca656af840dff83E8264EcF986CA).safeApprove(address(router), MAX_INT); // link
        IERC20(0xD533a949740bb3306d119CC777fa900bA034cd52).safeApprove(address(router), MAX_INT); // crv
        IERC20(0x6B175474E89094C44Da98b954EedeAC495271d0F).safeApprove(address(router), MAX_INT); // dai
        IERC20(0xA0b86991c6218b36c1d19D4a2e9Eb0cE3606eB48).safeApprove(address(router), MAX_INT); // usdc
        IERC20(0xdAC17F958D2ee523a2206206994597C13D831ec7).safeApprove(address(router), MAX_INT); // usdt
    }


    // simply arb lowest - highest and sell back to eth if profit threshold is reached
    function arb(address _lowestValueToken, address _highestValueToken, uint _profitThreshold) external onlyOwner {
        uint threshold = wmul(tradeAmount, _profitThreshold);
        // swap weth for lowest
        address[] memory path = new address[](2);
        path[0] = weth;
        path[1] = _lowestValueToken;

        (uint[] memory amountsL) = router.swapExactTokensForTokens(
            tradeAmount,
            0,
            path,
            address(this),
            block.timestamp
        );
        uint lowestAmount = amountsL[1];
        IERC20(_lowestValueToken).safeApprove(address(plaza), lowestAmount);
        uint highestAmount = plaza.swap(_lowestValueToken, _highestValueToken, lowestAmount, 0);
        IERC20(_lowestValueToken).safeApprove(address(plaza), 0);

        path[0] = _highestValueToken;
        path[1] = weth;
        (uint[] memory amountsH) = router.swapExactTokensForTokens(
            highestAmount,
            0,
            path,
            address(this),
            block.timestamp
        );

        /*
        console.log("arb() weth output: %s", amountsH[1]);
        console.log("arb() weth profit: %s", sub(amountsH[1], tradeAmount));
        */
        require(amountsH[1] >= threshold, "Threshold not met");
    }

    // set the amount of WETH used for trading
    function setTradeAmount(uint _newAmount) external onlyOwner {
        tradeAmount = _newAmount;
    }

    // sets uniswap approval for new token
    function addToken(address _newToken) external onlyOwner {
        IERC20(_newToken).safeApprove(address(router), MAX_INT);
    }

    function kill() external onlyOwner {
        // send weth just in case we haven't already
        uint bal = IERC20(weth).balanceOf(address(this));
        IERC20(weth).transfer(msg.sender, bal);
        // self destruct
        address payable addr = payable(address(msg.sender));
        selfdestruct(addr);
    }

    function transfer(address _token) external onlyOwner {
        uint bal = IERC20(_token).balanceOf(address(this));
        IERC20(_token).transfer(msg.sender, bal);
    }
}
