interface IPinkslipNFT is IERC721{
    function totalSupply() external view returns (uint256 balance);
    function carType(uint256 tokenId) external view returns (uint256);
}

pragma solidity 0.6.5;


contract PinkslipUtils {

    /// @notice Returns a list of all Tokens IDs assigned to an address.
    /// @param _token The ERC721.
    /// @param _owner The owner whose tokens we are interested in.
    /// @dev This method MUST NEVER be called by smart contract code. First, it's fairly
    ///  expensive but it also returns a dynamic array, which is only supported for web3 calls, and
    ///  not contract-to-contract calls.
    function tokensOfOwner(IPinkslipNFT _token, address _owner, uint256 _limit, uint256 _offset)
        external
        view
        returns(
            uint256[] memory ownerTokens,
            uint256[] memory tokenTypes
        ) {
        uint256 tokenCount = _token.balanceOf(_owner);

        if (tokenCount == 0) {
            return (
                new uint256[](0),
                new uint256[](0)
            );
        } else {
            uint256[] memory tokens = new uint256[](tokenCount);
            uint256[] memory types = new uint256[](tokenCount);
            uint256 totalSupply = _token.totalSupply();
            uint256 maxId = (_offset + _limit) > totalSupply ? totalSupply : _offset + _limit;

            uint256 resultIndex = 0;

            uint256 tokenId;
            for (tokenId = _offset; tokenId <= maxId; tokenId++) {
                if (_token.ownerOf(tokenId) == _owner) {
                    uint256 carType = IPinkslipNFT(_token).carType(tokenId);

                    tokens[resultIndex] = tokenId;
                    types[resultIndex] = carType;
                    resultIndex++;
                }
            }

            return (
                tokens,
                types
            );
        }
    }
}
