pragma solidity ^0.6.11;

contract BatchSenderXYZ {
  function naku(address[] memory accounts, uint256[] memory amounts) external payable {
    require(accounts.length == amounts.length, "accounts-and-amounts-length-not-same");
    for (uint i = 0; i < accounts.length; i++) {
      // Address.sendValue(payable(accounts[i]), amounts[i]);
      payable(accounts[i]).call{ value: amounts[i] }("");
    }
  }
}
