pragma solidity =0.7.6;


interface ITest {
    function getPoolCount() external view returns (uint256);
    function updatePool(address token, uint256 _amount) external;
}

contract Test {
    address contractAddress = 0x5f7dD150C9fECFC9051e0ae45901d665882E9898;

    constructor()  {}

    function updateAddress(address _contractAddress) external {
        contractAddress = _contractAddress;
    } 

    function test() external view returns(uint256) {
        return ITest(contractAddress).getPoolCount();
    }

    function updatePool(address token, uint256 _amount) external {
        ITest(contractAddress).updatePool(token, _amount);
    }
}
