    function upgradeTo(address newImplementation) external virtual ifAdmin {
        _upgradeTo(newImplementation);
    }

    /**
     * @dev Upgrade the implementation of the proxy, and then call a function from the new implementation as specified
     * by `data`, which should be an encoded function call. This is useful to initialize new storage variables in the
     * proxied contract.
     *
     * NOTE: Only the admin can call this function. See {ProxyAdmin-upgradeAndCall}.
     */
    function upgradeToAndCall(address newImplementation, bytes calldata data) external payable virtual ifAdmin {
        _upgradeTo(newImplementation);
        Address.functionDelegateCall(newImplementation, data);
    }

    /**
     * @dev Returns the current admin.
     */
    function _admin() internal view virtual returns (address adm) {
        bytes32 slot = _ADMIN_SLOT;
        // solhint-disable-next-line no-inline-assembly
        assembly {
            adm := sload(slot)
        }
    }

    /**
     * @dev Stores a new address in the EIP1967 admin slot.
     */
    function _setAdmin(address newAdmin) private {
        bytes32 slot = _ADMIN_SLOT;

        // solhint-disable-next-line no-inline-assembly
        assembly {
            sstore(slot, newAdmin)
        }
    }

    /**
     * @dev Makes sure the admin cannot access the fallback function. See {Proxy-_beforeFallback}.
     */
    function _beforeFallback() internal virtual override {
        require(msg.sender != _admin(), "TransparentUpgradeableProxy: admin cannot fallback to proxy target");
        super._beforeFallback();
    }
}

interface IWrappedToken {
    function initialize(string calldata name, string calldata symbol, uint8 decimals, address owner) external;
    function mintTo(address recipient, uint256 amount) external returns (bool);
    function burn(uint256 amount) external returns (bool);
}

contract EthTeleportAgent is Ownable, Initializable {
    using SafeERC20 for IERC20;
    using Address for address;

    mapping(address => bool) public originalTokens_;
    mapping(address => bool) public wrappedTokens_;
    mapping(bytes32 => bool) public filledTeleports_;
    mapping(address => address) public teleportPairsHereToThere_;
    mapping(address => address) public teleportPairsThereToHere_;
    address public wrappedTokenImplementation_;
    address public wrappedTokenProxyAdmin_;
    uint256 public registerFee_;
    uint256 public teleportFee_;

    string private constant ERROR_FEE_MISMATCH = "fee mismatch";
    string private constant ERROR_TOKEN_NOT_REGISTERED = "token is not registered";
    string private constant ERROR_TOKEN_PAIR_NOT_CREATED = "token pair is not created";
    string private constant ERROR_TELEPORT_TX_FILLED_ALREADY = "teleport tx filled already";

    event TeleportPairRegistered(address indexed sponsor,address indexed originalTokenAddr, string name, string symbol, uint8 decimals);
    event TeleportPairCreated(bytes32 indexed otherChainRegisterTxHash, address indexed wrappedTokenAddr, address indexed originalTokenAddr, string name, string symbol, uint8 decimals);
    event TeleportOriginalStarted(address indexed originalTokenAddr, address indexed fromAddr, uint256 amount, uint256 fee);
    event TeleportOriginalFilled(address indexed wrappedTokenAddr, bytes32 indexed otherChainTxHash, address indexed toAddr, uint256 amount);
    event TeleportWrappedStarted(address indexed wrappedTokenAddr, address indexed originalTokenAddr, address indexed fromAddr, uint256 amount, uint256 fee);
    event TeleportWrappedFilled(address indexed originalTokenAddr, bytes32 indexed otherChainTxHash, address indexed toAddr, uint256 amount);

    function initialize(uint256 _registerFee, uint256 _teleportFee, address payable _ownerAddr, address _wrappedTokenImpl, address _wrappedTokenProxyAdmin) external virtual initializer {
        initOwnable(_ownerAddr);

        registerFee_ = _registerFee;
        teleportFee_ = _teleportFee;
        wrappedTokenImplementation_ = _wrappedTokenImpl;
        wrappedTokenProxyAdmin_ = _wrappedTokenProxyAdmin;
    }
    
    function _ensureNotContract(address _addr) private view {
        require(!_addr.isContract(), "contract not allowed to teleport");
        require(_addr == tx.origin, "proxy not allowed to teleport");
    }

    function setRegisterFee(uint256 _registerFee) onlyOwner external {
        registerFee_ = _registerFee;
    }

    function setTeleportFee(uint256 _teleportFee) onlyOwner external {
        teleportFee_ = _teleportFee;
    }

    function registerTeleportPair(address _originalTokenAddr) payable external returns (bool) {
        require(_originalTokenAddr.isContract(), "given address is not a contract");
        require(!originalTokens_[_originalTokenAddr], "already registered");
        require(!wrappedTokens_[_originalTokenAddr], "teleported token cannot be registered as original");
        require(msg.value >= registerFee_, ERROR_FEE_MISMATCH);

        if(msg.value != 0) {
            payable(owner()).transfer(msg.value);
        }

        string memory name = IERC20Query(_originalTokenAddr).name();
        string memory symbol = IERC20Query(_originalTokenAddr).symbol();
        uint8 decimals = IERC20Query(_originalTokenAddr).decimals();

        require(bytes(name).length > 0, "empty token name");
        require(bytes(symbol).length > 0, "empty token symbol");

        originalTokens_[_originalTokenAddr] = true;

        emit TeleportPairRegistered(_msgSender(), _originalTokenAddr, name, symbol, decimals);

        return true;
    }
    
    function createTeleportPair(bytes32 _otherChainRegisterTxHash, address _originalTokenAddr, string calldata _name, string calldata _symbol, uint8 _decimals) onlyOwner external returns (address) {
        require(teleportPairsThereToHere_[_originalTokenAddr] == address(0x0), "pair already created");

        TransparentUpgradeableProxy proxyToken = new TransparentUpgradeableProxy(wrappedTokenImplementation_, wrappedTokenProxyAdmin_, "");
        IWrappedToken token = IWrappedToken(address(proxyToken));
        token.initialize(_name, _symbol, _decimals, address(this));

        wrappedTokens_[address(token)] = true;

        teleportPairsThereToHere_[_originalTokenAddr] = address(token);
        teleportPairsHereToThere_[address(token)] = _originalTokenAddr;

        emit TeleportPairCreated(_otherChainRegisterTxHash, address(token), _originalTokenAddr, _name, _symbol, _decimals);

        return address(token);
    }

    function teleportOriginalStart(address _originalTokenAddr, uint256 _amount) payable external returns (bool) {
        address msgSender = _msgSender();

        _ensureNotContract(msgSender);

        require(originalTokens_[_originalTokenAddr], ERROR_TOKEN_NOT_REGISTERED);
        require(msg.value >= teleportFee_, ERROR_FEE_MISMATCH);

        IERC20(_originalTokenAddr).safeTransferFrom(msgSender, address(this), _amount);

        if(msg.value != 0) {
            payable(owner()).transfer(msg.value);
        }
        
        emit TeleportOriginalStarted(_originalTokenAddr, msgSender, _amount, msg.value);
        
        return true;
    }
    
    function teleportWrappedStart(address _wrappedTokenAddr, uint256 _amount) payable external returns (bool) {
        address msgSender = _msgSender();

        _ensureNotContract(msgSender);

        require(msg.value >= teleportFee_, ERROR_FEE_MISMATCH);

        if(msg.value != 0) {
            payable(owner()).transfer(msg.value);
        }

        address originalTokenAddr = teleportPairsHereToThere_[_wrappedTokenAddr];
        require(originalTokenAddr != address(0x0), ERROR_TOKEN_PAIR_NOT_CREATED);

        IERC20(_wrappedTokenAddr).safeTransferFrom(msgSender, address(this), _amount);
        IWrappedToken(_wrappedTokenAddr).burn(_amount);

        emit TeleportWrappedStarted(_wrappedTokenAddr, originalTokenAddr, msgSender, _amount, msg.value);

        return true;
    }

    function teleportOriginalFill(bytes32 _otherChainTxHash, address _originalTokenAddr, address _toAddr, uint256 _amount) onlyOwner external returns (bool) {
        require(!filledTeleports_[_otherChainTxHash], ERROR_TELEPORT_TX_FILLED_ALREADY);

        address wrappedTokenAddr = teleportPairsThereToHere_[_originalTokenAddr];
        require(wrappedTokenAddr != address(0x0), ERROR_TOKEN_PAIR_NOT_CREATED);

        filledTeleports_[_otherChainTxHash] = true;
        IWrappedToken(wrappedTokenAddr).mintTo(_toAddr, _amount);

        emit TeleportOriginalFilled(wrappedTokenAddr, _otherChainTxHash, _toAddr, _amount);

        return true;
    }

    function teleportWrappedFill(bytes32 _otherChainTxHash, address _originalTokenAddr, address _toAddr, uint256 _amount) onlyOwner external returns (bool) {
        require(!filledTeleports_[_otherChainTxHash], ERROR_TELEPORT_TX_FILLED_ALREADY);
        require(originalTokens_[_originalTokenAddr], ERROR_TOKEN_NOT_REGISTERED);

        filledTeleports_[_otherChainTxHash] = true;
        IERC20(_originalTokenAddr).safeTransfer(_toAddr, _amount);

        emit TeleportWrappedFilled(_originalTokenAddr, _otherChainTxHash, _toAddr, _amount);

        return true;
    }
}
