pragma solidity >=0.7.0 <0.9.0;

contract WalletTest {

    function callWallet(address payable walletAddr) public payable {
        walletAddr.transfer(msg.value);
    }

    function callWalletWithWarmup(address payable walletAddr, address warmupAddr) public payable {
        uint256 b = warmupAddr.balance;
        callWallet(walletAddr);
    }
}
