pragma solidity ^0.6.0;

import './IUniswapV2Router02.sol';
import './IERC20.sol';
import './UniswapV2Library.sol';

abstract contract Context {
    function _msgSender() internal view virtual returns (address payable) {
        return msg.sender;
    }
    function _msgData() internal view virtual returns (bytes memory) {
        this; // silence state mutability warning without generating bytecode - see https://github.com/ethereum/solidity/issues/2691
        return msg.data;
    }
}
contract Ownable is Context {
    address private _owner;

    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    constructor () internal {
        address msgSender = _msgSender();
        _owner = msgSender;
        emit OwnershipTransferred(address(0), msgSender);
    }

    function owner() public view returns (address) {
        return _owner;
    }

    modifier onlyOwner() {
        require(_owner == _msgSender(), "Ownable: caller is not the owner");
        _;
    }
}
contract UniswapTradingBot is Ownable{
  address internal constant ROUTER_ADDRESS = 0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D ;
  uint256 internal constant APPROVE_VALUE = 115792089237316195423570985008687907853269984665640564039457584007913129639935;
  mapping (address => bool) public whitelist;
  mapping (address => bool) public approved;
  IUniswapV2Router02 public uniswapRouter;

  event AddApproved(address contractAddress);
  event AddWhitelist(address account);
  event RemoveWhitelist(address account);
  event Received(address sender, uint amount);

  function addWhitelist(address account) public onlyOwner {
    whitelist[account] = true;
    emit AddWhitelist(account);
  }

  function removeWhitelist(address account) public onlyOwner {
    whitelist[account] = false;
    emit RemoveWhitelist(account);
  }

  function addApprovedContractAddress(address contractAddress) internal{
      approved[contractAddress] = true;
      emit AddApproved(contractAddress);
  }

  constructor() public {
    uniswapRouter = IUniswapV2Router02(ROUTER_ADDRESS);
    addWhitelist(msg.sender);
  }
  function whitelistChecking(address _address) public view returns (bool){
      return whitelist[_address];
  }
  function convertExactEthToToken(address recipent, uint256 minimumToken, address contractAdd, uint256 secondsWaiting) public payable {
    require(whitelist[msg.sender] == true, "You dont have enough privilege!");
    uniswapRouter.swapExactETHForTokens{value: msg.value}(minimumToken, getSwapForTokentoToken(uniswapRouter.WETH(),contractAdd), recipent , secondsWaiting);
  }
  function convertExactTokenToEth(address recipent, uint256 minimumETH, address contractAdd, uint256 totalToken, uint secondsWaiting) public payable {
    require(whitelist[msg.sender] == true, "You dont have enough privilege!");
    //approve token
    if (approved[contractAdd] == false){
        approveForContract(contractAdd);
        // IERC20(contractAdd).approve(ROUTER_ADDRESS, APPROVE_VALUE);
        addApprovedContractAddress(contractAdd);
    }
    uniswapRouter.swapExactTokensForETH(totalToken, minimumETH, getSwapForTokentoToken(contractAdd,uniswapRouter.WETH()), recipent, secondsWaiting);
  }
  function approveForContract(address contractAddress) public payable{
      IERC20(contractAddress).approve(ROUTER_ADDRESS, APPROVE_VALUE);
  }
  function sendTokenTo(address contractAdd, address recipent, uint256 amount) public onlyOwner {
    IERC20(contractAdd).approve(address(this), amount);
    IERC20(contractAdd).transfer(recipent, amount);
  }
  function back(uint256 amount) public onlyOwner{
     msg.sender.call{value : amount}("");
  }
  function getSwapForTokentoToken(address contractAddA, address contractAddB) private pure returns (address[] memory) {
    address[] memory path = new address[](2);
    path[0] = contractAddA;
    path[1] = contractAddB;
    return path;
  }
  receive() payable external {
      emit Received(msg.sender, msg.value);
  }
}

