contract FlashSwap is IUniswapV2Callee{

    bool public test;
    address private owner;

    address weth_addr;

    IWETH  WETH;

    // event for EVM logging
    event OwnerSet(address indexed oldOwner, address indexed newOwner);

    // modifier to check if caller is owner
    modifier isOwner() {
        // If the first argument of 'require' evaluates to 'false', execution terminates and all
        // changes to the state and to Ether balances are reverted.
        // This used to consume all gas in old EVM versions, but not anymore.
        // It is often a good idea to use 'require' to check if functions are called correctly.
        // As a second argument, you can also provide an explanation about what went wrong.
        require(msg.sender == owner, "Caller is not owner");
        _;
    }

    /**
     * @dev Set contract deployer as owner
     */
    constructor() public {
        owner = msg.sender; // 'msg.sender' is sender of current call, contract deployer for a constructor
        emit OwnerSet(address(0), owner);
        //WETH = IWETH(IUniswapV2Router01(router1).WETH());
        test=true;

    }

    receive() external payable {}

    function setTest(bool _test) public isOwner {
        test = _test;
    }

    /**
     * @dev Change owner
     * @param newOwner address of new owner
     */
    function changeOwner(address newOwner) public isOwner {
        emit OwnerSet(owner, newOwner);
        owner = newOwner;
    }

    /**
     * @dev Return owner address 
     * @return address of owner
     */
    function getOwner() external view returns (address) {
        return owner;
    }

    fallback() payable external {}

    function withdraw(uint256 amount) public isOwner{
        msg.sender.transfer(amount);
    }

    function transfer(address token_addr,uint256 amount) public isOwner{
        IERC20 token = IERC20(token_addr);
        token.transfer(msg.sender,amount);
    }

    function flashSwap(address _from, address _to, address token0, address token1, uint amount,uint gas) external isOwner{
        address[] memory path = new address[](2);
        address[] memory exchange_path = new address[](2);
        path[0]=token0;
        path[1]=token1;
        exchange_path[0]=token1;
        exchange_path[1]=token0;
        IWETH eth;
        IERC20 token;
        if(token0 == weth_addr){
            require(test || UniswapV2Library.getAmountsIn(_from,amount+gas,exchange_path)[0] < UniswapV2Library.getAmountsOut(_to,amount,path)[1],"win less then gas");
            eth = IWETH(token0);
            token = IERC20(token1);
            IUniswapV2Pair(UniswapV2Library.pairFor(_from,token0,token1)).swap(amount+gas,0,address(this),abi.encode(_to,amount));
        }
        if(token1 == weth_addr){
            require(test || UniswapV2Library.getAmountsIn(_from,amount,exchange_path)[0]+gas < UniswapV2Library.getAmountsOut(_to,amount,path)[1],"win less then gas");
            eth = IWETH(token1);
            token = IERC20(token0);
            IUniswapV2Pair(UniswapV2Library.pairFor(_from,token0,token1)).swap(amount,0,address(this),abi.encode(_to,amount));
        }


    }
    function uniswapV2Call(address sender, uint amount0, uint amount1, bytes calldata data) external override {
        require(sender == owner, "Caller is not owner");
        address[] memory path = new address[](2);
        address[] memory exchange_path = new address[](2);
         // scope for token{0,1}, avoids stack too deep errors
        address token0 = IUniswapV2Pair(msg.sender).token0();
        address token1 = IUniswapV2Pair(msg.sender).token1();
        assert(msg.sender == UniswapV2Library.pairFor(IUniswapV2Pair(msg.sender).factory(),token0,token1)); // ensure that msg.sender is actually a V2 pair
        assert(amount0 == 0 || amount1 == 0); // this strategy is unidirectional
        path[0]=token0;
        path[1]=token1;
        exchange_path[0]=token1;
        exchange_path[1]=token0;


        assert(path[0] == weth_addr || path[1] == weth_addr); // this strategy only works with a V2 WETH pair
        IERC20 token = IERC20(token0);
        //address factory_from = IUniswapV2Pair(msg.sender).factory();

        (address _to, uint amount) = abi.decode(data, (address,uint)); // slippage parameter for V1, passed in by caller

        //IUniswapV2Factory factory_to = IUniswapV2Factory(_to);
        address pair_to = UniswapV2Library.pairFor(_to,token0,token1);

        uint amount1Out = UniswapV2Library.getAmountsOut(_to,amount,path)[1];
        token.transfer(pair_to,amount);
        IUniswapV2Pair(pair_to).swap(0,amount1Out,address(this),new bytes(0));


    }
