// SPDX-License-Identifier: MIT
pragma solidity 0.8.5;

library Math {
    function min(uint256 a, uint256 b) internal pure returns (uint256) {
        return a < b ? a : b;
    }
}

interface IERC20 {
    function transfer(address recipient, uint256 amount) external returns (bool);
    function decimals() external view returns (uint256);
    function symbol() external view returns (string memory);
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);
}

interface Oracle {
    
    struct ReferenceData {
        uint256 rate; // base/quote exchange rate, multiplied by 1e18.
        uint256 lastUpdatedBase; // UNIX epoch of the last time when base price gets updated.
        uint256 lastUpdatedQuote; // UNIX epoch of the last time when quote price gets updated.
    }
    
    function getReferenceData(string memory _base, string memory _quote)
        external
        view
        returns (ReferenceData memory);
}

interface cToken {
    function underlying() external view returns (address);
}

interface comptroller {
    function getAllMarkets() external view returns (address[] memory);
    function markets(address _market) external view returns (bool isListed, uint256 collateralFactorMantissa, bool isComped);
}

interface ibtroller {
    function getAllMarkets() external view returns (address[] memory);
    function markets(address _market) external view returns (bool isListed, uint256 collateralFactorMantissa);
}

interface aavecore {
    struct ReserveConfigurationMap {
        //bit 0-15: LTV
        //bit 16-31: Liq. threshold
        //bit 32-47: Liq. bonus
        //bit 48-55: Decimals
        //bit 56: Reserve is active
        //bit 57: reserve is frozen
        //bit 58: borrowing is enabled
        //bit 59: stable rate borrowing enabled
        //bit 60-63: reserved
        //bit 64-79: reserve factor
        uint256 data;
    }
    function getReserveConfiguration(address _market) external view returns (uint, uint, uint, bool);
    function getConfiguration(address _market) external view returns (ReserveConfigurationMap memory);
}

contract ReferenceUSD {
    string public constant name = "Reference USD";
    string public constant symbol = "rUSD";
    uint8 public constant decimals = 18;
    
    address constant public oracle = 0xDA7a001b254CD22e46d3eAB04d937489c93174C3;
    address constant public stable = 0x6B175474E89094C44Da98b954EedeAC495271d0F;
    string constant public quote = "USD";
    
    uint public constant BASE = 100;
    uint public constant LIQUIDATION_VALUE = 90;
    uint public constant CACHE = 1 days;
    
    /// @notice Total number of tokens in circulation
    uint public totalSupply = 0;
    
    mapping(address => mapping (address => uint)) internal allowances;
    mapping(address => uint) internal balances;
    
    /// @notice The standard EIP-20 transfer event
    event Transfer(address indexed from, address indexed to, uint amount);

    /// @notice The standard EIP-20 approval event
    event Approval(address indexed owner, address indexed spender, uint amount);
    
    mapping(address => mapping(address => uint)) public credit;
    mapping(address => mapping(address => uint)) public collateral;
    
    mapping(address => uint) public ltvs;
    mapping(address => uint) public ltvCaches;
    
    uint public arb;
    
    
    function validateLTV(uint __ltv) public pure returns (bool) {
        if ((__ltv == 80) || (__ltv == 70) || (__ltv == 60)) {
            return true;
        } else if ((__ltv == 50) || (__ltv == 85) || (__ltv == 65)) {
            return true;
        } else if (__ltv >= 90) {
            return true;
        } else if (__ltv == 75) {
            return true;
        } else {
            return false;
        }
    }
    
    function gentleRepaymentCalculator(uint __ltv, uint debt, uint value) public pure returns (uint repayment) {
        if (__ltv == 80) {
            return Math.min((debt - value) * 910 / BASE, debt);
        } else if (__ltv == 70) {
            return Math.min((debt - value) * 460 / BASE, debt);
        } else if (__ltv == 60) {
            return Math.min((debt - value) * 310 / BASE, debt);
        } else if (__ltv == 50) {
            return Math.min((debt - value) * 235 / BASE, debt);
        } else if (__ltv == 85) {
            return Math.min((debt - value) * 1810 / BASE, debt);
        } else if (__ltv == 65) {
            return Math.min((debt - value) * 370 / BASE, debt);
        } else if (__ltv == 75) {
            return Math.min((debt - value) * 610 / BASE, debt);
        } else if (__ltv >= 90) {
            return debt;
        }
    }
    
    function _lookup(address quoted, uint amount) internal returns (uint) {
        return Oracle(oracle).getReferenceData(IERC20(quoted).symbol(), quote).rate * (amount * _ltv(quoted) / BASE) / 10 ** IERC20(quoted).decimals();
    }
    
    function lookup(address quoted, uint amount) external view returns (uint) {
        return Oracle(oracle).getReferenceData(IERC20(quoted).symbol(), quote).rate * (amount * ltv(quoted) / BASE) / 10 ** IERC20(quoted).decimals();
    }
    
    function lookupLiq(address quoted, uint amount) public view returns (uint) {
        return Oracle(oracle).getReferenceData(IERC20(quoted).symbol(), quote).rate * (amount * LIQUIDATION_VALUE / BASE) / 10 ** IERC20(quoted).decimals();
    }
    
    function mintArb(uint amount, address recipient) external {
        _safeTransferFrom(stable, msg.sender, address(this), amount);
        _mint(recipient, amount);
        arb += amount;
    }
    
    function burnArb(uint amount, address recipient) external {
        _burn(msg.sender, amount);
        _safeTransfer(stable, recipient, amount);
        arb -= amount;
    }
    
    function mint(address asset, uint amount, uint minted, address recipient) external {
        if (amount > 0) {
            _safeTransferFrom(asset, msg.sender, address(this), amount);
        }
        
        collateral[msg.sender][asset] += amount;
        credit[msg.sender][asset] += minted;
        
        require(_lookup(asset, collateral[msg.sender][asset]) >= credit[msg.sender][asset]);
        _mint(recipient, minted);
    }
    
    function burn(address asset, uint amount, uint burned, address recipient) external {
        _burn(msg.sender, burned);
        
        credit[msg.sender][asset] -= burned;
        collateral[msg.sender][asset] -= amount;
        
        require(_lookup(asset, collateral[msg.sender][asset]) >= credit[msg.sender][asset]);
        
        if (amount > 0) {
            _safeTransfer(asset, recipient, amount);
        }
    }
    
    function liquididate(address owner, address asset) external {
        uint _nominal = collateral[owner][asset];
        
        uint _backed = _lookup(asset, _nominal);
        uint _debt = credit[owner][asset];
        require(_backed < _debt);
        
        uint _repayment = gentleRepaymentCalculator(ltv(asset), _debt, _backed);
        uint _payment = Math.min(_nominal * _repayment / lookupLiq(asset, _nominal), _nominal);
        
        _burn(msg.sender, _repayment);
        
        credit[owner][asset] -= _repayment;
        collateral[owner][asset] -= _payment;
        
        require(_lookup(asset, collateral[owner][asset]) >= credit[owner][asset]);
        
        _safeTransfer(asset, msg.sender, _payment);
    }
    
    function liquididateArb(address owner, address asset) external {
        uint _nominal = collateral[owner][asset];
        
        uint _backed = _lookup(asset, _nominal);
        uint _debt = credit[owner][asset];
        require(_backed < _debt);
        
        uint _repayment = gentleRepaymentCalculator(ltv(asset), _debt, _backed);
        uint _payment = Math.min(_nominal * _repayment / lookupLiq(asset, _nominal), _nominal);
        
        _safeTransferFrom(stable, msg.sender, address(this), _repayment);
        arb += _repayment;
        
        credit[owner][asset] -= _repayment;
        collateral[owner][asset] -= _payment;
        
        require(_lookup(asset, collateral[owner][asset]) >= credit[owner][asset]);
        
        _safeTransfer(asset, msg.sender, _payment);
    }
    
    function _mint(address dst, uint amount) internal {
        // mint the amount
        totalSupply += amount;
        // transfer the amount to the recipient
        balances[dst] += amount;
        emit Transfer(address(0), dst, amount);
    }
    
    function _burn(address dst, uint amount) internal {
        // burn the amount
        totalSupply -= amount;
        // transfer the amount from the recipient
        balances[dst] -= amount;
        emit Transfer(dst, address(0), amount);
    }
    
    function allowance(address account, address spender) external view returns (uint) {
        return allowances[account][spender];
    }

    function approve(address spender, uint amount) external returns (bool) {
        allowances[msg.sender][spender] = amount;

        emit Approval(msg.sender, spender, amount);
        return true;
    }

    function balanceOf(address account) external view returns (uint) {
        return balances[account];
    }

    function transfer(address dst, uint amount) external returns (bool) {
        _transferTokens(msg.sender, dst, amount);
        return true;
    }

    function transferFrom(address src, address dst, uint amount) external returns (bool) {
        address spender = msg.sender;
        uint spenderAllowance = allowances[src][spender];

        if (spender != src && spenderAllowance != type(uint).max) {
            uint newAllowance = spenderAllowance - amount;
            allowances[src][spender] = newAllowance;

            emit Approval(src, spender, newAllowance);
        }

        _transferTokens(src, dst, amount);
        return true;
    }

    function _transferTokens(address src, address dst, uint amount) internal {
        balances[src] -= amount;
        balances[dst] += amount;
        
        emit Transfer(src, dst, amount);
    }
    
    function _safeTransfer(
        address token,
        address to,
        uint256 value
    ) internal {
        (bool success, bytes memory data) =
            token.call(abi.encodeWithSelector(IERC20.transfer.selector, to, value));
        require(success && (data.length == 0 || abi.decode(data, (bool))));
    }
    
    function _safeTransferFrom(
        address token,
        address from,
        address to,
        uint256 value
    ) internal {
        (bool success, bytes memory data) =
            token.call(abi.encodeWithSelector(IERC20.transferFrom.selector, from, to, value));
        require(success && (data.length == 0 || abi.decode(data, (bool))));
    }
    
    address constant private _cream = address(0x3d5BC3c8d13dcB8bF317092d84783c2697AE9258);
    address constant private _compound = address(0x3d9819210A31b4961b30EF54bE2aeD79B9c9Cd3B);
    address constant private _aavev1 = address(0x3dfd23A6c5E8BbcFc9581d2E864a68feb6a076d3);
    address constant private _aavev2 = address(0x7d2768dE32b0b80b7a3454c06BdAc94A69DDc7A9);
    address constant private _ib = address(0xAB1c342C7bf5Ec5F02ADEA1c2270670bCa144CbB);
    address constant private _weth = address(0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2);
    
    uint256 constant private _LTV_MASK =                   0xFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFF0000; // prettier-ignore
    
    function _getParamsMemory(aavecore.ReserveConfigurationMap memory self) internal pure returns (uint256) { 
        return (self.data & ~_LTV_MASK);
    }
    
    function _lookupMarket(address _core, address _token) internal view returns (address) {
        if (_core == _compound && _token == _weth) {
            return 0x4Ddc2D193948926D02f9B1fE9e1daa0718270ED5;
        } else if (_core == _cream && _token == _weth) {
            return 0xD06527D5e56A3495252A528C4987003b712860eE;
        }
        address[] memory _list = comptroller(_core).getAllMarkets();
        for (uint i = 0; i < _list.length; i++) {
            if (_list[i] != address(0x4Ddc2D193948926D02f9B1fE9e1daa0718270ED5) && _list[i] != address(0xD06527D5e56A3495252A528C4987003b712860eE)) {
                if (cToken(_list[i]).underlying() == _token) {
                    return _list[i];
                }
            }
        }
        return address(0x0);
    }
    
    function _getLTVCream(address token) internal view returns (uint cream) {
        (,cream,) = comptroller(_cream).markets(_lookupMarket(_cream, token));
        cream = cream / 1e16;
    }
    
    function _getLTVCompound(address token) internal view returns (uint compound) {
        (,compound,) = comptroller(_compound).markets(_lookupMarket(_compound, token));
        compound = compound / 1e16;
    }
    
    function _getLTVIB(address token) internal view returns (uint ib) {
        (,ib) = ibtroller(_ib).markets(_lookupMarket(_ib, token));
        ib = ib / 1e16;
    }
    
    function _getLTVAaveV1(address token) internal view returns (uint aavev1) {
        (,aavev1,,) = aavecore(_aavev1).getReserveConfiguration(token);
    }
    
    function _getLTVAaveV2(address token) internal view returns (uint aavev2) {
        (aavev2) = _getParamsMemory(aavecore(_aavev2).getConfiguration(token));
        aavev2 = aavev2 / 1e2;
    }
    
    function _ltv(address token) internal returns (uint) {
        if (block.timestamp > ltvCaches[token]) {
            ltvCaches[token] = block.timestamp + CACHE;
            uint _max = 0;
            uint _tmp = _getLTVCream(token);
            if (_tmp > _max) {
                _max = _tmp;
            }
            _tmp = _getLTVCompound(token);
            if (_tmp > _max) {
                _max = _tmp;
            }
            _tmp = _getLTVIB(token);
            if (_tmp > _max) {
                _max = _tmp;
            }
            _tmp = _getLTVAaveV1(token);
            if (_tmp > _max) {
                _max = _tmp;
            }
            _tmp = _getLTVAaveV2(token);
            if (_tmp > _max) {
                _max = _tmp;
            }
            _max = _max / 5 * 5;
            if (_max < 50) {
                _max = 0;
            }
            ltvs[token] = _max;
            return _max;
        } else {
            return ltvs[token];
        }
    }
    
    function ltv(address token) public view returns (uint) {
        uint _max = 0;
        uint _tmp = _getLTVCream(token);
        if (_tmp > _max) {
            _max = _tmp;
        }
        _tmp = _getLTVCompound(token);
        if (_tmp > _max) {
            _max = _tmp;
        }
        _tmp = _getLTVIB(token);
        if (_tmp > _max) {
            _max = _tmp;
        }
        _tmp = _getLTVAaveV1(token);
        if (_tmp > _max) {
            _max = _tmp;
        }
        _tmp = _getLTVAaveV2(token);
        if (_tmp > _max) {
            _max = _tmp;
        }
        _max = _max / 5 * 5;
        if (_max < 50) {
            _max = 0;
        }
        return _max;
    }
}
