// SPDX-License-Identifier: MIT
pragma solidity ^0.6.0;

abstract contract ChildBombers {
     function setTarget(address payable _to) virtual external;
}

contract BomberMan {
    address payable public owner;
    constructor() public{
        owner = msg.sender;
    }
    receive()external payable{}
    fallback()external payable{}
    function sweep()public{
        require(msg.sender == owner);
        owner.transfer(address(this).balance);
    }
    // master 0x983e0FbD51b9dfcdb790A6a5e7DD99A61493ea86
    function bomb(address master, uint256 count,address payable target, uint256 amount) external payable {
            for(uint256 o =0; o < count; o++) {
                deployChild(master,target,amount);
            }
    }
    function deployChild(address master, address payable target, uint256 amount) internal {
           address payable bomber = createClone(master);
           ChildBombers(bomber).setTarget(target);
           bomber.transfer(amount);
           selfdestruct(owner);
    }
    function createClone(address master) internal returns (address payable result) {
        bytes20 targetBytes = bytes20(master);
        assembly {
          let clone := mload(0x40)
          mstore(clone, 0x3d602d80600a3d3981f3363d3d373d3d3d363d73000000000000000000000000)
          mstore(add(clone, 0x14), targetBytes)
          mstore(add(clone, 0x28), 0x5af43d82803e903d91602b57fd5bf30000000000000000000000000000000000)
          result := create(0, clone, 0x37)
      }
    }

}
