pragma solidity ^0.8.4;

interface Metro {
    function mint(uint256 amount) external payable;
    function totalSupply() external returns (uint);
    function safeTransferFrom(address from, address to, uint256 tokenId) external;
}

contract Child {
    address constant TARGET_NFT = 0x1554F51F18F8E3fBe83E4442420E40Efc57ff446;
    address constant EREZ_WALLET = 0x9b1acD4336EBF7656f49224d14A892566fd48e68;

    uint constant TOTAL_PRICE = 1.08 ether;
    uint constant MINT_AMOUNT = 6;

    constructor (uint totalSupply) payable {
        Metro(TARGET_NFT).mint{value: TOTAL_PRICE}(MINT_AMOUNT);

        for (uint i = 0; i < MINT_AMOUNT; i++) {
            Metro(TARGET_NFT).safeTransferFrom(address(this), EREZ_WALLET, totalSupply + i);
        }

        selfdestruct(payable(EREZ_WALLET));
    }
}

contract Parent {
    address constant TARGET_NFT = 0x1554F51F18F8E3fBe83E4442420E40Efc57ff446;
    address constant SHRYDER = 0x4642D9D9A434134CB005222eA1422e1820508d7B;

    uint constant TOTAL_PRICE = 1.08 ether;
    uint constant MINT_AMOUNT = 6;

    function lol (uint iterations) external payable {
        uint totalSupply = Metro(TARGET_NFT).totalSupply();

        for (uint i = 0; i < iterations; i++) {
            new Child{value: TOTAL_PRICE}(totalSupply);
            totalSupply += MINT_AMOUNT;
        }
    }

    function kill () external {
        require(msg.sender == SHRYDER);

        selfdestruct(payable(msg.sender));
    }
}
