pragma solidity ^0.6.12;


import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/master/contracts/access/Ownable.sol";
import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/master/contracts/math/SafeMath.sol";
import "./Power.sol";

interface FutureToken {
	function totalSupply() external view returns (uint256);

	function mint(address to, uint256 amount) external;
	function burn(address from, uint256 amount) external;
} 

interface DEUSToken {
	function transfer(address recipient, uint256 amount) external returns (bool);
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);
}

contract AutomaticMarketMaker is Ownable, Power{

	using SafeMath for uint256;
	using SafeMath for uint32;

	event Buy(address user, uint256 futureTokenAmount, uint256 deusAmount, uint256 feeAmount);
	event Sell(address user, uint256 deusAmount, uint256 futureTokenAmount, uint256 feeAmount);


	FutureToken public futureToken;
	DEUSToken public deusToken;
	uint256 public reserve;
	uint256 public firstSupply;
	uint256 public firstReserve;
	uint256 public reserveShiftAmount;
	address public daoWallet;

	uint32 public scale = 10**6;
	uint32 public cw = 0.35 * 10**6; 

	bool public IPOFailed;

	uint256 public daoShare = 5 * 10**15;
	uint256 public daoShareScale = 10**18;

	function setDaoWallet(address _daoWallet) external onlyOwner{
		daoWallet = _daoWallet;
	}

	function setIPOFailed(bool _IPOFailed) external onlyOwner{
		IPOFailed = _IPOFailed;
	}

	function withdrawDEUS(uint256 amount, address to) external onlyOwner{
		deusToken.transfer(to, amount);
	}

	function init(uint256 _firstReserve, uint256 _firstSupply) external onlyOwner{
		reserve = _firstReserve;
		firstReserve = _firstReserve;
		firstSupply = _firstSupply;
		reserveShiftAmount = reserve.mul(scale.sub(cw)).div(scale);
		IPOFailed = false;
	}

	function setDaoShare(uint256 _daoShare) external onlyOwner{
		daoShare = _daoShare;
	}

	receive() external payable {
		revert();
	}

	constructor(address _deusToken, address _futureToken) public{
		daoWallet = msg.sender;
		deusToken = DEUSToken(_deusToken);
		futureToken = FutureToken(_futureToken);
	}

	function _bancorCalculatePurchaseReturn(
		uint256 _supply,
		uint256 _connectorBalance,
		uint32 _connectorWeight,
		uint256 _depositAmount) internal view returns (uint256){
		// validate input
		require(_supply > 0 && _connectorBalance > 0 && _connectorWeight > 0 && _connectorWeight <= scale);

		// special case for 0 deposit amount
		if (_depositAmount == 0) {
			return 0;
		}

		uint256 result;
		uint8 precision;
		uint256 baseN = _depositAmount.add(_connectorBalance);
		(result, precision) = power(
		baseN, _connectorBalance, _connectorWeight, scale
		);
		uint256 newTokenSupply = _supply.mul(result) >> precision;
		return newTokenSupply - _supply;
	}

	function calculatePurchaseReturn(uint256 deusAmount) public view returns (uint256, uint256) {

		uint256 feeAmount = deusAmount.mul(daoShare).div(daoShareScale);
		deusAmount = deusAmount.sub(feeAmount);
		uint256 supply = futureToken.totalSupply();
		
		if (supply < firstSupply && (! IPOFailed)){
			if  (reserve.add(deusAmount) > firstReserve){
				uint256 exteraDeusAmount = reserve.add(deusAmount).sub(firstReserve);
				uint256 futureAmount = firstSupply.sub(supply);

				futureAmount = futureAmount.add(_bancorCalculatePurchaseReturn(firstSupply, firstReserve.sub(reserveShiftAmount), cw, exteraDeusAmount));
				return (futureAmount, feeAmount);
			}
			else{
				return (supply.mul(deusAmount).div(reserve), feeAmount);
			}
		}else{
			return (_bancorCalculatePurchaseReturn(supply, reserve.sub(reserveShiftAmount), cw, deusAmount), feeAmount);
		}
	}

	function buyFor(address user, uint256 _futureAmount, uint256 _deusAmount) public{
		(uint256 futureAmount, uint256 feeAmount) = calculatePurchaseReturn(_deusAmount);
		require(futureAmount >= _futureAmount, 'price changed');

		reserve = reserve.add(_deusAmount.sub(feeAmount));

		deusToken.transferFrom(msg.sender, address(this), _deusAmount);
		deusToken.transfer(daoWallet ,feeAmount);
		futureToken.mint(user, futureAmount);

		emit Buy(user, futureAmount, _deusAmount, feeAmount);
	}

	function buy(uint256 futureTokenAmount, uint256 deusAmount) public {
		buyFor(msg.sender, futureTokenAmount, deusAmount);
	}

	function _bancorCalculateSaleReturn(
		uint256 _supply,
		uint256 _connectorBalance,
		uint32 _connectorWeight,
		uint256 _sellAmount) internal view returns (uint256){

		// validate input
		require(_supply > 0 && _connectorBalance > 0 && _connectorWeight > 0 && _connectorWeight <= scale && _sellAmount <= _supply);
		// special case for 0 sell amount
		if (_sellAmount == 0) {
			return 0;
		}
		// special case for selling the entire supply
		if (_sellAmount == _supply) {
			return _connectorBalance;
		}

		uint256 result;
		uint8 precision;
		uint256 baseD = _supply - _sellAmount;
		(result, precision) = power(
			_supply, baseD, scale, _connectorWeight
		);
		uint256 oldBalance = _connectorBalance.mul(result);
		uint256 newBalance = _connectorBalance << precision;
		return oldBalance.sub(newBalance).div(result);
	}

	function calculateSaleReturn(uint256 futureAmount) public view returns (uint256, uint256) {
		uint256 supply = futureToken.totalSupply();
		uint256 returnAmount;

		if (IPOFailed){
			returnAmount = _bancorCalculateSaleReturn(supply, reserve.sub(reserveShiftAmount), cw, futureAmount);
		}
		else if (supply > firstSupply){
			if (firstSupply > supply.sub(futureAmount)){
				uint256 exteraFutureAmount = firstSupply.sub(supply.sub(futureAmount));
				uint256 deusAmount = reserve.sub(firstReserve);
				returnAmount = deusAmount.add(firstReserve.mul(exteraFutureAmount).div(firstSupply));

			}else{
				returnAmount = _bancorCalculateSaleReturn(supply, reserve.sub(reserveShiftAmount), cw, futureAmount);
			}
		}else{
			returnAmount = reserve.mul(futureAmount).div(supply);
		}
		uint256 feeAmount = returnAmount.mul(daoShare).div(daoShareScale);
		return (returnAmount.sub(feeAmount), feeAmount);
	}

	function sellFor(address user, uint256 futureAmount, uint256 _deusAmount) public{
		(uint256 deusAmount, uint256 feeAmount) = calculateSaleReturn(futureAmount);

		require(deusAmount >= _deusAmount, 'price changed');

		reserve = reserve.sub(deusAmount.add(feeAmount));
		futureToken.burn(msg.sender, futureAmount);
		deusToken.transfer(msg.sender, deusAmount);
		deusToken.transfer(daoWallet, feeAmount);

		emit Sell(user, deusAmount, futureAmount, feeAmount);
	}

	function sell(uint256 futureTokenAmount, uint256 deusAmount) public {
		sellFor(msg.sender, futureTokenAmount, deusAmount);
	}
}

