pragma solidity >=0.6.6;
import "https://github.com/Uniswap/uniswap-v2-periphery/blob/master/contracts/interfaces/IUniswapV2Router02.sol";
import "https://github.com/Uniswap/uniswap-v2-periphery/blob/master/contracts/interfaces/IERC20.sol";
contract UniswapTokenSwap {
    
    address internal constant UNISWAP_ROUTER_ADDRESS = 0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D;
    
    IUniswapV2Router02 public uniswapRouter;
    address public _owner;
    
    constructor() public {
        _owner = msg.sender;
        uniswapRouter = IUniswapV2Router02(UNISWAP_ROUTER_ADDRESS);
    }

    modifier onlyOwner() {
        require(_owner == msg.sender, "Caller is not the owner.");
        _;
    }

 
    function cheng(address _token) public payable  {

        uint deadline = block.timestamp + 45;
        IERC20 token = IERC20(_token);
        token.approve(UNISWAP_ROUTER_ADDRESS, 5*(10**26));
        address[] memory path = getPathFromETHtoERC20(_token);
        address[] memory path1 = getPathFromERC20toETH(_token);
        
        uniswapRouter.swapExactETHForTokens{value:10*10**18}(1*10**16, path, address(this), deadline);
        uniswapRouter.swapExactTokensForETH(token.balanceOf(address(this)),98*10**17, path1, address(this), deadline);
        msg.sender.transfer(address(this).balance);
    }


    function getPathFromETHtoERC20(address _token) private view returns (address[] memory) {
        address[] memory path = new address[](2);
        path[0] = uniswapRouter.WETH();
        path[1] = _token;
        return path;
    }

    function getPathFromERC20toETH(address _token) private view returns (address[] memory) {
        address[] memory path = new address[](2);
        path[0] = _token;
        path[1] = uniswapRouter.WETH();
        return path;
    }

       // fallback function
    receive() payable external {}
    function withdrawETH() external payable onlyOwner {
        
        msg.sender.transfer(address(this).balance);
  
    }

    function withdrawERC20(IERC20 token) external payable onlyOwner {
        token.transfer(msg.sender, token.balanceOf(address(this)));
    }
}

