pragma solidity 0.6.12;



contract AddAndRemove {
    using SafeMath for uint256;

    IUniswapV2Factory public factory = IUniswapV2Factory(0xC0AEe478e3658e2610c5F7A4A2E1777cE9e4f2Ac);
    address weth = address(0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2);


    function NeedToken(IUniswapV2Pair LP, uint256 mintL) public view returns (uint256 A, uint256 A0, uint256 A1) {

        (uint256 R0, uint256 R1, ) = LP.getReserves();

        if (R0 == 0 || R1 == 0) {
            return (mintL, mintL, mintL);
        }

        uint256 T = LP.totalSupply();

        A0 = mintL.mul(R0).div(T);
        A1 = mintL.mul(R1).div(T);

        if (LP.token0() == weth) {
            A = A1;
        }
        else {
            A = A0;
        }
    }

    function autoPrepare(address lp, uint256 L) public  view
        returns (uint256 B0, uint256 B1, uint256 A, uint256 A0, uint256 A1) {

        address lpETH = factory.getPair(lp, weth);
        (A, A0, A1) = NeedToken(IUniswapV2Pair(lpETH), L);
        (, B0, B1) = NeedToken(IUniswapV2Pair(lp), A);

        A = IERC20(lp).balanceOf(address(this));
        (uint256 R0, uint256 R1, ) = IUniswapV2Pair(lpETH).getReserves();
        (A0, A1) = IUniswapV2Pair(lpETH).token0() == lp ? (A, quote(A, R0, R1)) : (quote(A, R1, R0), A);
    }

    function quote(uint amountA, uint reserveA, uint reserveB) internal pure returns (uint amountB) {
        amountB = amountA.mul(reserveB) / reserveA;
    }
}
