// File: rebalancer.sol


pragma solidity 0.7.5;







contract MigrationRebalancer is OlympusAccessControlled {

    using SafeMath for uint256;

    IStaking public immutable staking;
    IERC20 public immutable ohm;
    IsOHM public immutable sohm;
    ITreasury public immutable treasury;

    bool public rebalanced;

    constructor(
        address _staking,
        address _ohm,
        address _sohm,
        address _treasury,
        address _authority
    ) OlympusAccessControlled(IOlympusAuthority(_authority)) {
        require(_staking != address(0), "Zero address: Staking");
        staking = IStaking(_staking);
        require(_ohm != address(0), "Zero address: Staking");
        ohm = IERC20(_ohm);
        require(_sohm != address(0), "Zero address: Staking");
        sohm = IsOHM(_sohm);
        require(_treasury != address(0), "Zero address: Staking");
        treasury = ITreasury(_treasury);
        rebalanced = false;
    }

    // This function rebalances the staking contract following migration.
    // Because the migrator mints the entire staked supply as migrate-able gOHM,
    // an imbalance may arise in which less OHM liquidity is available than 
    // staked tokens outstanding. By calculating the imbalance between circulating
    // sOHM supply (sOHM outstanding + sOHM value of gOHM outstanding + tokens in warmup),
    // we can easily fix this imbalance. The inflated circulating staked supply
    // can then be corrected by migrating DAO-held OHM through the migrator to acquire
    // gOHM and unstake it back to OHM.
    function rebalance() external onlyGovernor {
        require(!rebalanced, "Already rebalanced");
        treasury.mint(address(staking), imbalance());
        rebalanced = true;
    }

    // view imbalance to be corrected
    function imbalance() public view returns (uint256) {
        uint256 ohmInStaking = ohm.balanceOf(address(staking));
        return sohm.circulatingSupply().sub(ohmInStaking);
    }
}
