contract ICO is Context , Ownable {
    using SafeMath for uint256;
    // The token being sold
    IERC20 private _token;

    // Address where funds are collected
    address payable private _wallet;
    address private _tokenWallet;

    // How many token units a buyer gets per wei.
    // The rate is the conversion between wei and the smallest and indivisible token unit.
    // So, if you are using a rate of 1 with a ERC20Detailed token with 3 decimals called TOK
    // 1 wei will give you 1 unit, or 0.001 TOK.
    uint256 private _rate;

    // Amount of wei raised
    uint256 private _weiRaised;

    uint256 private _tokensSold;

    struct Referral {
        address marketer;
        uint256 referralBonus;
    }
    // mapping to get referrer address from the referral code.
    mapping (uint256=>Referral) public _referrer;

    // mapping to get referral code from the address.
    mapping (address=>uint256) public _code;

    // Referral code Tracker
    // uint256 public _referralCode = 0;
    // uint256 public _referralBonus = 5;
    uint256 public _buyerBonus = 5;

    struct UserInfo {
        uint256 funds;
    }

    mapping (address => UserInfo) users;

    /**
     * Event for token purchase logging
     * @param purchaser who paid for the tokens
     * @param beneficiary who got the tokens
     * @param value weis paid for purchase
     * @param amount amount of tokens purchased
     */
    event TokensPurchased(address indexed purchaser, address indexed beneficiary, uint256 value, uint256 amount);

    /**
     * @dev The rate is the conversion between wei and the smallest and indivisible
     * token unit. So, if you are using a rate of 1 with a ERC20Detailed token
     * with 3 decimals called TOK, 1 wei will give you 1 unit, or 0.001 TOK.
     * wallet Address where collected funds will be forwarded to
     * token Address of the token being sold
     */
    constructor () {
        _tokenWallet = 0x30F222478e2B5934501FC448604fE6e061EDd2BC;
        _wallet = payable(0x5D23061F330c045755aB7f7572e13F5Da463d8D5); //main
        _token = IERC20(0xBee237ED8D61fa1268CF7297bB3C13f131493Eea); // main
        _rate = 40000;
    }

    /**
     * @dev fallback function ***DO NOT OVERRIDE***
     * Note that other contracts will transfer funds with a base gas stipend
     * of 2300, which is not enough to call buyTokens. Consider calling
     * buyTokens directly when purchasing tokens from a contract.
     */
    fallback () external payable {
        buyTokens(_msgSender(), 0);
    }

    receive () external payable {
        buyTokens(_msgSender(), 0);
    }

    /**
     * @return the funds rasied by user
     */
    function userInfo(address user) public view returns (uint256) {
        return users[user].funds;
    }

    /**
     * @return the token being sold.
     */
    function gettoken() public view returns (IERC20) {
        return _token;
    }

    /**
     * @return the address where funds are collected.
     */
    function getwallet() public view returns (address payable) {
        return _wallet;
    }

    /**
     * @return the number of token units a buyer gets per wei.
     */
    function getrate() public view returns (uint256) {
        return _rate;
    }

    /**
     * @return the amount of wei raised.
     */
    function weiRaised() public view returns (uint256) {
        return _weiRaised;
    }

    /**
     * @return The code assigned to Marketer
     * 0 code means, no code assigned
     */
    function getCode(address marketer) public view returns (uint256) {
        return _code[marketer];
    }

    function setRBonus (address marketer_, uint256 referralBonus_) public onlyOwner returns (bool) {
        uint256 code = _code[marketer_];
        require(code != 0, "This address does not have a code");
        _referrer[code].referralBonus = referralBonus_;
        return true;
    }
    function setBuyerBonus (uint256 buyerBonus_) public onlyOwner returns (bool) {
        _buyerBonus = buyerBonus_;
        return true;
    }
    function setToken (IERC20 token) public onlyOwner returns (bool) {
        _token = token;
        return true;
    }
    function setRate (uint256 rate) public onlyOwner returns (bool) {
        _rate = rate;
        return true;
    }
    function setWallet (address payable wallet ) public onlyOwner returns (bool) {
        _wallet = wallet;
        return true;
    }
    function setTokenWallet (address wallet ) public onlyOwner returns (bool) {
        _tokenWallet = wallet;
        return true;
    }
    function random() private view returns (uint) {
        // sha3 and now have been deprecated
        return uint(keccak256(abi.encodePacked(block.difficulty, block.timestamp)));
        // convert hash to integer        
    }
    /**
     * @param marketer_ Address to assign a referral code
     * @param referralBonus_ bonus percentage for the specific marketer
     * @return The code assigned to Marketer
     */
    function generateCode(address marketer_, uint256 referralBonus_) public onlyOwner returns (uint256) {
        require(_code[marketer_] == 0, "This address has already a code");
        uint256 _referralCode = random() % 100000;
        while(_referrer[_referralCode].marketer != address(0))
        {
            _referralCode = random() % 100000;
        }
        _code[marketer_] = _referralCode;
        _referrer[_referralCode].marketer = marketer_;
        _referrer[_referralCode].referralBonus = referralBonus_;
        return _referralCode;
    }

    /**
     * @dev low level token purchase ***DO NOT OVERRIDE***
     * This function has a non-reentrancy guard, so it shouldn't be called by
     * another `nonReentrant` function.
     * @param beneficiary Recipient of the token purchase
     */
    function buyTokens(address beneficiary, uint256 referralCode_) public payable {
        uint256 weiAmount = msg.value;
        _preValidatePurchase(beneficiary, weiAmount);

        users[beneficiary].funds += weiAmount;

        // calculate token amount to be created
        uint256 tokens = _getTokenAmount(weiAmount);

        if(referralCode_ != 0) { // with referral 
            address referrer = _referrer[referralCode_].marketer;
            uint256 Rbonus = _getRBonusAmount(tokens, referralCode_);
            uint256 Bbonus = _getBBonusAmount(tokens);

            tokens = tokens + Bbonus;
            _processPurchase(referrer, Rbonus);
        }

        // update state
        _weiRaised = _weiRaised.add(weiAmount);

        _processPurchase(beneficiary, tokens);
        emit TokensPurchased(_msgSender(), beneficiary, weiAmount, tokens);
        _forwardFunds();

    }

    /**
     * @dev Validation of an incoming purchase. Use require statements to revert state when conditions are not met.
     * Use `super` in contracts that inherit from Crowdsale to extend their validations.
     * Example from CappedCrowdsale.sol's _preValidatePurchase method:
     *     super._preValidatePurchase(beneficiary, weiAmount);
     *     require(weiRaised().add(weiAmount) <= cap);
     * @param beneficiary Address performing the token purchase
     * @param weiAmount Value in wei involved in the purchase
     */
    function _preValidatePurchase(address beneficiary, uint256 weiAmount) internal view {
        require(beneficiary != address(0), "Crowdsale: beneficiary is the zero address");
        require(weiAmount != 0, "Crowdsale: weiAmount is 0");
        this; // silence state mutability warning without generating bytecode - see https://github.com/ethereum/solidity/issues/2691
    }


    /**
     * @dev Source of tokens. Override this method to modify the way in which the crowdsale ultimately gets and sends
     * its tokens.
     * @param beneficiary Address performing the token purchase
     * @param tokenAmount Number of tokens to be emitted
     */
    function _deliverTokens(address beneficiary, uint256 tokenAmount) internal {
        _token.transferFrom(_tokenWallet, beneficiary, tokenAmount); // wallet which will approve the tokens 
        _tokensSold = _tokensSold + tokenAmount;
    }

    /**
     * @dev Executed when a purchase has been validated and is ready to be executed. Doesn't necessarily emit/send
     * tokens.
     * @param beneficiary Address receiving the tokens
     * @param tokenAmount Number of tokens to be purchased
     */
    function _processPurchase(address beneficiary, uint256 tokenAmount) internal {
        _deliverTokens(beneficiary, tokenAmount);
    }


    /**
     * @dev Override to extend the way in which ether is converted to tokens.
     * @param weiAmount Value in wei to be converted into tokens
     * @return Number of tokens that can be purchased with the specified _weiAmount
     */
    function _getTokenAmount(uint256 weiAmount) internal view returns (uint256) {  
        return weiAmount.mul(_rate);
    }

    /**
     * @dev Override to extend the way in which ether is converted to tokens.
     * @param tokens Value in wei to be converted into tokens
     * @param _referralCode referral code
     * @return Number of tokens that can be purchased with the specified _weiAmount
     */
    function _getRBonusAmount(uint256 tokens, uint256 _referralCode) internal view returns (uint256) {  
        return tokens.mul(_referrer[_referralCode].referralBonus).div(100);
    }
        /**
     * @dev Override to extend the way in which ether is converted to tokens.
     * @param tokens Value in wei to be converted into tokens
     * @return Number of tokens that can be purchased with the specified _weiAmount
     */
    function _getBBonusAmount(uint256 tokens) internal view returns (uint256) {  
        return tokens.mul(_buyerBonus).div(100);
    }

    /**
     * @dev Determines how ETH is stored/forwarded on purchases.
     */
    function _forwardFunds() internal {
        _wallet.transfer(msg.value);
    }
}
