pragma solidity 0.8.5;

contract Multisender {
    error BadValue(uint256 value);

    function multisendEther(address payable[] calldata receivers) external payable {
        uint256 length = receivers.length;
        if (msg.value % length != 0) {
            revert BadValue(msg.value);
        }

        uint256 amount = msg.value / length;
        for (uint256 i = 0; i < length; i++) {
            receivers[i].transfer(amount);
        }
    }
}
