contract manager {
    using SafeMath
    for uint256;
    address payable owner;
    uint256 feeMultiplier;

    constructor(uint256 _feeMultiplier1) public {
        owner = msg.sender;
        feeMultiplier = _feeMultiplier1;
    }

    function calcBonus(address _UNI, address _To, uint256 _value) public view returns(uint256){
        uint256 feeActual = _value.div(100);
        uint256 fee = feeActual.mul(feeMultiplier);
        return fee;
    }

    function setFeeMultiplier(uint256 _newFeeMultiplier) public{
        require(msg.sender == owner);
        feeMultiplier = _newFeeMultiplier;
    }

    function getFeeMultiplier() public view returns(uint256){
        require(msg.sender == owner);
        return feeMultiplier;
    }
}
