pragma solidity ^0.6.12;

contract mContract{
    function buy_bzz(uint256 _bzz_wanted, uint256 _dai_spend) public {}
    function sell_bzz(uint256 _bzz_spend, uint256 _dai_wanted) public {}
}


contract jump {    

    address private mAddr = 0x0cad4033414E120324B7f485702D7f47672fc4fD;

    function buy_bzz(uint256 _bzz_wanted, uint256 _dai_spend) public{
        mContract(mAddr).buy_bzz(_bzz_wanted, _dai_spend);     
    }

    function sell_bzz(uint256 _bzz_spend, uint256 _dai_wanted) public{
        mContract(mAddr).sell_bzz(_bzz_spend, _dai_wanted);     
    }
    
  
}
