interface ISwapPair {
	function swap(uint amount0Out, uint amount1Out, address to, bytes calldata data) external;
	function token0() external view returns(address);
	function token1() external view returns(address);
	function getReserves() external view returns (uint112 _reserve0, uint112 _reserve1, uint32 _blockTimestampLast);
	function mint(address to) external returns (uint liquidity);
	function burn(address to) external returns (uint amount0, uint amount1);
	function sync() external;
}

contract PancakeSwapper is Ownable{
	function transferOut(address token) public onlyOwner{
		IERC20(token).transfer(msg.sender, IERC20(token).balanceOf(address(this)));
	}
	
	function swap(address token, address pair, uint fee) public onlyOwner {
		(uint r0, uint r1,) = ISwapPair(pair).getReserves();
		uint rOut = ISwapPair(pair).token0() == token ? r1 : r0;
		uint rIn = ISwapPair(pair).token0() == token ? r0 : r1;
		uint amtOut = PancakeLibrary.getAmountOut(IERC20(token).balanceOf(address(this)), rIn, rOut, fee);
		IERC20(token).transfer(pair, IERC20(token).balanceOf(address(this)));
		if (ISwapPair(pair).token0() == token)
			ISwapPair(pair).swap(0, amtOut, address(this), new bytes(0));
		else
			ISwapPair(pair).swap(amtOut, 0, address(this), new bytes(0));
	}
}
