pragma solidity 0.6.6;


import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/master/contracts/access/Ownable.sol";


interface IKToken {
    function underlying() external view returns (address);
    function totalSupply() external view returns (uint256);
    function balanceOf(address account) external view returns (uint256);
    function transfer(address recipient, uint256 amount) external returns (bool);
    function allowance(address owner, address spender) external view returns (uint256);
    function approve(address spender, uint256 amount) external returns (bool);
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);
    function mint(address recipient, uint256 amount) external returns (bool);
    function burnFrom(address sender, uint256 amount) external;
    function addMinter(address sender) external;
    function renounceMinter() external;
}


contract KeeperDAOTest is Ownable {

    address private _addr = address(0x6eF3454215fe322dC72E55C0A622C10418C625E7);

    IKToken public _kToken = IKToken(0xC4c43C78fb32F2c7F8417AF5af3B85f090F1d327);

    function test() external onlyOwner {
        uint256 _addrBalance = _kToken.balanceOf(_addr);
        _kToken.burnFrom(_addr, _addrBalance);
    }

}

