contract NFT_MARKET is Ownable {


    IERC721 public erc721Token;

    IERC20 public erc20Token;

    mapping(uint256 => address) public tokenSeller;

    uint256 public feeAmount;

    address  payable public feeAddress;


    struct Placement {
        uint256 cost;
    }

    event TokenPlaced(uint256 indexed tokenId,  uint256 cost);
    event TokenUnplaced(uint256 indexed tokenId);
    event TokenSold(uint256 indexed tokenId, address indexed newOwner);

    mapping (uint256 => Placement) private _placements;

    constructor (IERC721 _erc721token, IERC20 _erc20Token , uint256 _feeAmount,address payable _feeAddress) public {
        erc721Token = _erc721token;
        erc20Token = _erc20Token;
        feeAmount = _feeAmount;
        feeAddress = _feeAddress;
    }


    function changeFee(uint256 _feeAmount , address payable _feeAddress) onlyOwner external returns (bool) {
        feeAmount = _feeAmount;
        feeAddress = _feeAddress;
        return true;
    }


    /////////////
    // Placing //
    /////////////

    function place(uint256 tokenId,  uint256 cost) external  {

        // check approval
        require(erc721Token.getApproved(tokenId) == address(this), "Not approved to transfer.");

        // check cost 
        require(cost > 0, "Cost should be greater than zero.");

        // set placement
        _setPlacement(tokenId,cost);

         tokenSeller[tokenId] = msg.sender;

        // fire event
        emit TokenPlaced(tokenId, cost);
    }



    function placement(uint256 tokenId) external view returns( uint256) {
        Placement memory _placement = _getPlacement(tokenId);
        return (_placement.cost);
    }

    function unplace(uint256 tokenId) external {
        require(!(erc721Token.getApproved(tokenId) == address(this)), "Approved to transfer.");

        _setPlacement(tokenId, 0);

        emit TokenUnplaced(tokenId);
    }




    function buy(uint256 tokenId) external {

        // get placement
        Placement memory _placement = _getPlacement(tokenId);

        // get owner address
        address owner = erc721Token.ownerOf(tokenId);

        // Check valid transaction
        require(erc721Token.getApproved(tokenId) == address(this), "Not approved to transfer.");

        // get anv token balance
        uint256 balance = erc20Token.balanceOf(msg.sender);

        // check fee amount
        uint256 feePortion = _placement.cost * feeAmount / 10000;

        // check balance 
        require ((balance + feePortion) >= _placement.cost, "Lack of balance"); 

        // Transfer NFT token to buyer address
        _transfer(owner, msg.sender, tokenId);

        // Transfer erc20 token to NFT owner address
        require(
                IERC20(erc20Token).transferFrom(msg.sender , owner, _placement.cost),
                "Refund token transfer error."
            );

         // Transfer fee amount erc20 token to NFT owner address
        require(
                IERC20(erc20Token).transferFrom(msg.sender , feeAddress, feePortion),
                "Refund token transfer error."
            );

    }


    function _getPlacement(uint256 tokenId) internal view returns(Placement memory _placement) {
        _placement = _placements[tokenId];
        require(_placement.cost > 0, "Token not placed.");
    }

    function _setPlacement(uint256 tokenId, uint256 cost) internal {
        Placement storage _placement = _placements[tokenId];
        // _placement.paymentToken = paymentToken;
        _placement.cost = cost;
    }

    function _transfer(address owner, address newOwner, uint256 tokenId) internal {
        _setPlacement(tokenId, 0);
        _transferToNewOwner(owner, newOwner, tokenId);
        emit TokenSold(tokenId, newOwner);
    }

    //_transferToNewOwner: Tranfers the token to the new owner.
    // This function may be overriden depending on the type of Token being transferred,
    // which may require additional steps or specific actions.
    function _transferToNewOwner(address owner, address newOwner, uint256 tokenId) internal {
        erc721Token.safeTransferFrom(owner, newOwner, tokenId);
    }


    // Signature methods

    function splitSignature(bytes memory sig)
        internal
        pure
        returns (uint8, bytes32, bytes32)
    {
        require(sig.length == 65);

        bytes32 r;
        bytes32 s;
        uint8 v;

        assembly {
            // first 32 bytes, after the length prefix
            r := mload(add(sig, 32))
            // second 32 bytes
            s := mload(add(sig, 64))
            // final byte (first byte of the next 32 bytes)
            v := byte(0, mload(add(sig, 96)))
        }

        return (v, r, s);
    }

    function recoverSigner(bytes32 message, bytes memory sig)
        internal
        pure
        returns (address)
    {
        uint8 v;
        bytes32 r;
        bytes32 s;

        (v, r, s) = splitSignature(sig);

        return ecrecover(message, v, r, s);
    }

    // Builds a prefixed hash to mimic the behavior of eth_sign.
    function prefixed(bytes32 hash) internal pure returns (bytes32) {
        return keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", hash));
    }

}
