interface ISWAP {
    function SingleSwap(address _from_token, address _to_token, 
                        uint256 _from_amt, uint256 _max_slippage, uint256 _deadline,
                        address payable sender, address payable receiver) external payable returns(uint256);
    function MultiSwapList( address [] memory _fromToken_list, address [] memory _toToken_list, 
                            uint256 [] memory _fromTokenAmt_list, 
                            uint256 _max_slippage, uint256 _deadline,
                            address payable sender, address payable receiver) external payable returns(uint256[] memory);
}

