contract Uniswap_Trade_V2 is WhitelistedRole, Ownable, ReentrancyGuard{
    using SafeERC20 for IERC20;
	using SafeMath for uint256;

    // Main Network
    address public lp       = address(0);
    address public swap     = address(0);
    address public price    = address(0);
    // Info
    mapping (address => address) public Delegate;  // User, Trader
    mapping (address => address []) public InvestTokenList;
    mapping (address => uint256) public DepositETH;
    mapping (address => mapping (address => uint256)) public InvestTokenAMT;   // User, Token, amount
    mapping (address => mapping (address => uint256)) public InvestTokenUSD;   // User, Token, USD
    mapping (address => mapping (address => uint256)) public InvestTokenETH;   // User, Token, ETH
    
    event BuyEvent(address investor, address pair, uint256 lp_amt, uint256 amt_eth, uint256 usd);
    event SellEvent(address investor, address pair, uint256 lp_amt, uint256 amt_eth, uint256 usd);
    ///////////////////////////////////////////////////////////////////////////////////////////////////////////
    constructor(address _swap, address _lp, address _price) public{
        swap    = _swap;
        lp      = _lp;
        price   = _price;
    }
    function SetConfig(address _target1, address _target2) public onlyOwner{
        swap = _target1;
        lp = _target2;
    }
    ///////////////////////////////////////////////////////////////////////////////////////////////////////////
    function SetDelegate(address delegate) external {
        Delegate[msg.sender] = delegate;
    }
    function CheckDelegate(address investor, address delegate) external view returns(bool){
        if(Delegate[investor] == delegate){
            return true;
        }else{
            return false;
        }
    }
    ///////////////////////////////////////////////////////////////////////////////////////////////////////////

    ///////////////////////////////////////////////////////////////////////////////////////////////////////////
    function Deposit() public payable nonReentrant{
        DepositETH[msg.sender] = DepositETH[msg.sender].add(msg.value);
    }
    function Withdraw(uint256 amt) public payable nonReentrant{
        if(DepositETH[msg.sender] >= amt){
            DepositETH[msg.sender] = DepositETH[msg.sender].sub(amt);
            payable(msg.sender).transfer(amt);
        }
    }
    ///////////////////////////////////////////////////////////////////////////////////////////////////////////
    function AddTokenInfo(address _investor, uint256 _eth_amt, address _token, uint256 _token_amt, uint256 usd) internal{
        _eth_amt = _eth_amt.mul(10**18);
        InvestTokenETH[_investor][_token] = ((InvestTokenETH[_investor][_token].mul(InvestTokenAMT[_investor][_token])).add(_eth_amt)).div(InvestTokenAMT[_investor][_token].add(_token_amt));
        InvestTokenUSD[_investor][_token] = ((InvestTokenUSD[_investor][_token].mul(InvestTokenAMT[_investor][_token])).add(_eth_amt.mul(usd))).div(InvestTokenAMT[_investor][_token].add(_token_amt));
        InvestTokenAMT[_investor][_token] = InvestTokenAMT[_investor][_token].add(_token_amt);
        
        bool isNew = false;
        for (uint256 pid = 0; pid < InvestTokenList[_investor].length; ++pid) {
            if(InvestTokenList[_investor][pid] == _token){
                isNew = true;
                break;
            }
        }
        if(isNew==false){
            InvestTokenList[_investor].push(_token);
        }
    }

    function SubTokenInfo(address _investor, address _token, uint256 _token_amt) internal{
        InvestTokenAMT[_investor][_token] = InvestTokenAMT[_investor][_token].sub(_token_amt);
        if(InvestTokenAMT[_investor][_token] == 0){
            for (uint256 pid = 0; pid < InvestTokenList[_investor].length; ++pid) {
                if(InvestTokenList[_investor][pid] == _token){
                    delete InvestTokenList[_investor][pid];
                    break;
                }
            }
        }
    }
    function isValidETH(address investor, uint256 eth_amt, uint256 get_eth) internal returns(bool res){
        if(this.CheckDelegate(investor, msg.sender)){
            if(DepositETH[investor] >= eth_amt){
                DepositETH[investor] = DepositETH[investor].sub(eth_amt);
                res = true;
            }
        }else if(msg.sender == investor && eth_amt <= get_eth){
            res = true;
        }
    }
    ///////////////////////////////////////////////////////////////////////////////////////////////////////////
    function BuyLP(
        address[] memory lp_list, uint256[] memory eth_list, 
        uint256 max_slippage, uint256 deadline,
        address payable investor
    ) external payable nonReentrant returns(uint256[] memory num_tokens){
        require(eth_list.length == lp_list.length, "BuyLP:1");
        require(msg.sender == investor || this.CheckDelegate(investor, msg.sender), "BuyLP:2");
        
        //Commission
        uint256 eth_amt = 0;
        uint256[] memory pre_balance = new uint256[](lp_list.length);
        for(uint i = 0; i < lp_list.length; i++){
            eth_list[i] = IPRICE(price).UpfrontCommission(eth_list[i]);
            eth_amt = eth_amt.add(eth_list[i]);
            pre_balance[i] = IERC20(lp_list[i]).balanceOf(address(this));
        }
        
       
        bool is_valid = isValidETH(investor, eth_amt, msg.value);
        require(is_valid, "BuyLP:3");

        //BUY ACT
        //num_tokens = new uint256[](lp_list.length);
        num_tokens = ILP(lp).StakeList{ value: eth_amt}(lp_list, eth_list, 
                                                        max_slippage, deadline,
                                                        address(this), address(this));
        for(uint i = 0; i < lp_list.length; i++){
            num_tokens[i] = IERC20(lp_list[i]).balanceOf(address(this)).sub(pre_balance[i]);
        }
                                                
        //UPDATE WALLET
        for(uint i = 0; i < lp_list.length; i++){
            AddTokenInfo(investor, eth_list[i], lp_list[i], num_tokens[i], IPRICE(price).USD());
            emit BuyEvent(investor, lp_list[i], num_tokens[i], eth_list[i], IPRICE(price).USD());
        }
    }

    function BuyToken(
        address[] memory token_list, uint256[] memory eth_list, 
        uint256 max_slippage, uint256 deadline,
        address payable investor
    ) external payable nonReentrant returns(uint256[] memory num_tokens){
        require(eth_list.length == token_list.length, "BuyToken:1");
        require(msg.sender == investor || this.CheckDelegate(investor, msg.sender), "BuyToken:2");
        
        //Commission
        uint256 eth_amt = 0;
        uint256[] memory pre_balance = new uint256[](token_list.length);
        address[] memory from_list = new address[](token_list.length);
        for(uint i = 0; i < token_list.length; i++){
            eth_list[i] = IPRICE(price).UpfrontCommission(eth_list[i]);
            eth_amt = eth_amt.add(eth_list[i]);
            from_list[i] = IPRICE(price).WETH();
            pre_balance[i] = IERC20(token_list[i]).balanceOf(address(this));
        }
        
        bool is_valid = isValidETH(investor, eth_amt, msg.value);
        require(is_valid, "BuyToken:3");


        //BUY ACT 
        num_tokens = ISWAP(swap).MultiSwapList{ value: eth_amt}( from_list, token_list,
                                                                eth_list,
                                                                max_slippage, deadline,
                                                                address(this), address(this));
        for(uint i = 0; i < token_list.length; i++){
            num_tokens[i] = IERC20(token_list[i]).balanceOf(address(this)).sub(pre_balance[i]);
        }
        
        //UPDATE WALLET
        for(uint i = 0; i < token_list.length; i++){
            AddTokenInfo(investor, eth_list[i], token_list[i], num_tokens[i], IPRICE(price).USD());
            emit BuyEvent(investor, token_list[i], num_tokens[i], eth_list[i], IPRICE(price).USD());
        }
    }

    function SellLP(
        address[] memory lp_list, uint256[] memory lp_amt_list,
        uint256 max_slippage, uint256 deadline,
        address payable investor, address payable receiver
    ) external payable nonReentrant returns(uint256[] memory num_tokens){
        require(lp_amt_list.length == lp_list.length, "SellLP:1");
        require(msg.sender == investor || this.CheckDelegate(investor, msg.sender), "SellLP:2");
        
        //Check balance
        for(uint i = 0; i < lp_list.length; i++){
            require(lp_amt_list[i] <= InvestTokenAMT[investor][lp_list[i]], "SellLP:3");
            IERC20(lp_list[i]).safeApprove(lp, lp_amt_list[i]);
        }
        
        //SELL ACT
        //num_tokens = new uint256[](lp_list.length);
        num_tokens = ILP(lp).UnstakeList(lp_list, lp_amt_list, 
                                        max_slippage, deadline,
                                        address(this), address(this));
                                                
        //UPDATE WALLET
        uint256 eth_amt = 0;
        for(uint i = 0; i < lp_list.length; i++){
            eth_amt = eth_amt.add(num_tokens[i]);
            SubTokenInfo(investor, lp_list[i], lp_amt_list[i]);
            emit SellEvent(investor, lp_list[i], lp_amt_list[i], num_tokens[i], IPRICE(price).USD());
        }
        eth_amt = IPRICE(price).TrailCommission(eth_amt);
        payable(receiver).transfer(eth_amt);
    }

    function SellToken(
        address[] memory token_list, uint256[] memory token_amt_list, 
        uint256 max_slippage, uint256 deadline,
        address payable investor, address payable receiver
    ) external payable nonReentrant returns(uint256[] memory num_tokens){
        require(token_amt_list.length == token_list.length, "SellToken:1");
        require(msg.sender == investor || this.CheckDelegate(investor, msg.sender), "SellToken:2");
        
        //Check balance
        address[] memory to_list = new address[](token_list.length);
        for(uint i = 0; i < token_list.length; i++){
            require(token_amt_list[i] <= InvestTokenAMT[investor][token_list[i]], "Insufficent token balance");
            to_list[i] = IPRICE(price).WETH();
            IERC20(token_list[i]).safeApprove(swap, token_amt_list[i]);
        }
        
        //SELL ACT
        num_tokens = ISWAP(swap).MultiSwapList(token_list, to_list, 
                                            token_amt_list,
                                            max_slippage, deadline,
                                            address(this), address(this));
                                                
        //UPDATE WALLET
        uint256 eth_amt = 0;
        for(uint i = 0; i < token_list.length; i++){
            SubTokenInfo(investor, token_list[i], token_amt_list[i]);
            eth_amt = eth_amt.add(num_tokens[i]);
            emit SellEvent(investor, token_list[i], token_amt_list[i], num_tokens[i], IPRICE(price).USD());
        }
        eth_amt = IPRICE(price).TrailCommission(eth_amt);
        payable(receiver).transfer(eth_amt);
    }
