pragma solidity 0.6.12;

import "./SafeMath.sol";
import "./ISwap.sol";
import "./IERC20.sol";

/**
* @title App - The inRate application.
* @notice This contract is responsible for non-custody helper functions
* which provide UX improvements to inRate.
*/

contract App {

    using SafeMath for uint256; // include safe math utils

    address public ADMIN = 0x3D4EE6C6e48069247Cac2836301bE0E439abAf9c; // the administrator address

    uint256 public FEE = 100000000000000000000; // the standard fee per transaction ~ $0.10 per transaction

    //@dev takes multiple tokens and sends them to an address

    function MultiTokenSend(
        address[] calldata tokens,
        uint256[] calldata amounts,
        address recipient
    ) public returns (bool) {
        require(recipient != 0x0000000000000000000000000000000000000000);
        for (uint256 i = 0; i < tokens.length; i++) {
            IERC20 t = IERC20(tokens[i]);
            t.transferFrom(msg.sender, address(this), amounts[i]);
            t.transfer(recipient, t.balanceOf(address(this)));
        }
        return true;
    }

    //@dev takes a single token, splits it up and sends it to multiple addresses

    // function MultiAddressSend(
    //     address token,
    //     uint256 amount,
    //     address[] calldata recipients
    // ) public returns (bool) {
    //     IERC20 t = IERC20(token);
    //     t.transferFrom(msg.sender, address(this), amount);
    //     t.transfer(admin, formatDecimals(fee, 18, t.decimals()));
    //     for (uint256 i = 0; i <= recipients.length; i++) {
    //         require(recipients[i] != 0x0000000000000000000000000000000000000000);
    //         uint256 newAmount = t.balanceOf(address(this));
    //         uint256 split = newAmount.div(recipients.length.sub(1));
    //         t.transfer(recipients[i], split);
    //         i++;
    //     }
    //     t.transfer(msg.sender, t.balanceOf(address(this)));
    //     return true;
    // }

    //@dev swaps tokens and transfers the converted tokens to an address

    function SwapAndTransfer(
        address swap,
        uint8 tokenIndexFrom,
        uint8 tokenIndexTo,
        uint256 dx,
        uint256 minDy,
        uint256 deadline,
        address recipient
    ) public returns (bool) {

        require(recipient != 0x0000000000000000000000000000000000000000);

        ISwap s = ISwap(swap);

        IERC20 fromToken = s.getToken(tokenIndexFrom);

        IERC20 toToken = s.getToken(tokenIndexTo);

        fromToken.transferFrom(msg.sender, address(this), dx);

        fromToken.approve(swap, dx);

        uint256 amount = s.swap(tokenIndexFrom, tokenIndexTo, dx, minDy, deadline);

        toToken.transfer(recipient, amount);

        return true;

    }

    //@dev adds liquidity and transfers the LP balance to an address

    function AddLiquidityAndTransfer(
        address swap,
        address lpToken,
        uint256[] calldata amounts,
        uint256 minToMint,
        uint256 deadline,
        address recipient
    ) public returns (bool) {

        require(recipient != 0x0000000000000000000000000000000000000000);

        ISwap s = ISwap(swap);

        for (uint8 i = 0; i <= amounts.length; i++) {

            IERC20 t = s.getToken(i);

            t.transferFrom(msg.sender, address(this), amounts[i]);

            t.approve(swap, amounts[i]);

        }

        uint256 num = s.addLiquidity(amounts, minToMint, deadline);

        IERC20 lpt = IERC20(lpToken);

        lpt.transfer(recipient, num);

        return true;

    }

    //@dev removes liquidity and transfers the tokens to an address

    function RemoveLiquidityAndTransfer(
        address swap,
        address lpToken,
        uint256 amount,
        uint256[] calldata minAmounts,
        uint256 deadline,
        address recipient
    ) public returns (bool) {

        require(recipient != 0x0000000000000000000000000000000000000000);

        ISwap s = ISwap(swap);

        IERC20 lpt = IERC20(lpToken);

        lpt.transferFrom(msg.sender, address(this), amount);

        lpt.approve(swap, amount);

        uint256[] memory nums = s.removeLiquidity(amount, minAmounts, deadline);

        for (uint8 i = 0; i < nums.length; i++) {

            IERC20 t = s.getToken(i);

            t.transfer(recipient, nums[i]);

        }

        return true;
    }

    //@dev removes liquidity in the form of one token and then transfers it to an address

    function RemoveLiquidityOneTokenAndTransfer(
        address swap,
        address lpToken,
        uint256 tokenAmount,
        uint8 tokenIndex,
        uint256 minAmount,
        uint256 deadline,
        address recipient
    ) public returns (bool) {
        require(recipient != 0x0000000000000000000000000000000000000000);

        ISwap s = ISwap(swap);

        IERC20 lpt = IERC20(lpToken);

        lpt.transferFrom(msg.sender, address(this), tokenAmount);

        lpt.approve(swap, tokenAmount);

        uint256 num = s.removeLiquidityOneToken(tokenAmount, tokenIndex, minAmount, deadline);

        IERC20 t = s.getToken(tokenIndex);

        t.transfer(recipient, num);

        return true;
    }

    //@dev gets the quote for the multi to one swaps

    function QuoteMultiToOne(
        address swap,
        uint8[] calldata tokenIndexFrom,
        uint8 tokenIndexTo,
        uint256[] calldata dx
    ) external view returns (uint256) {
        ISwap s = ISwap(swap);

        uint256 amount;

        for(uint256 i; i < tokenIndexFrom.length; i++) {
            amount += s.calculateSwap(tokenIndexFrom[i], tokenIndexTo, dx[i]);
        }
        return amount;
    }

    // function QuoteMultiToOneReverse(
    //     address swap,
    //     uint8[] calldata tokenIndexFrom,
    //     uint8 tokenIndexTo,
    //     uint256 calldata dx
    // ) external view returns (uint256[] memory) {

    // }

    //@dev swaps multiple tokens to a single one

    function SwapMultiToOne(
        address swap,
        uint8[] calldata tokenIndexFrom,
        uint8 tokenIndexTo,
        uint256[] calldata dx,
        uint256[] calldata minDy,
        uint256 deadline
    ) public returns (bool) {

        ISwap s = ISwap(swap);

        for(uint256 i = 0; i < tokenIndexFrom.length; i++) {

            IERC20 fromToken = s.getToken(tokenIndexFrom[i]);

            fromToken.transferFrom(msg.sender, address(this), dx[i]);

            fromToken.approve(swap, dx[i]);

            s.swap(tokenIndexFrom[i], tokenIndexTo, dx[i], minDy[i], deadline);

        }

        IERC20 toToken = s.getToken(tokenIndexTo);

        toToken.transfer(msg.sender, toToken.balanceOf(address(this)));

        return true;
    }

    //@dev gets the quote for swaping one to multiple

    function QuoteOneToMulti(
        address swap,
        uint8 tokenIndexFrom,
        uint8[] calldata tokenIndexTo,
        uint256 dx
    ) external view returns (uint256[] memory) {
        ISwap s = ISwap(swap);

        uint256 split = dx.div(tokenIndexTo.length.sub(1));

        uint256[] memory amounts;

        for(uint256 i; i < tokenIndexTo.length; i++) {
            amounts[i] = s.calculateSwap(tokenIndexFrom, tokenIndexTo[i], split);
        }

        return amounts;
    }


    //@dev swaps one token to multiple tokens

    function SwapOneToMulti(
        address swap,
        uint8 tokenIndexFrom,
        uint8[] calldata tokenIndexTo,
        uint256 dx,
        uint256[] calldata minDy,
        uint256 deadline
    ) public returns (bool) {

        ISwap s = ISwap(swap);

        IERC20 fromToken = s.getToken(tokenIndexFrom);

        fromToken.transferFrom(msg.sender, address(this), dx);

        fromToken.approve(swap, dx);

        uint256 split = dx.div(tokenIndexTo.length.sub(1));


        for(uint256 i = 0; i < tokenIndexTo.length; i++) {

            s.swap(tokenIndexFrom, tokenIndexTo[i], split, minDy[i], deadline);

            s.swap(tokenIndexFrom, tokenIndexTo[i], fromToken.balanceOf(address(this)), 0, deadline);

            IERC20 toToken = s.getToken(tokenIndexTo[i]);

            toToken.transfer(msg.sender, toToken.balanceOf(address(this)));

        }

        return true;
    }

    // //@dev swaps multiple tokens at the same time

    // function MultiSwap(
