// SPDX-License-Identifier: GPL-3.0
pragma solidity >=0.7.0 <0.9.0;

/**
 * @title Storage
 * @dev Store & retrieve value in a variable
 */
 contract Spreader {
     function spread(address[] calldata a,uint256 x) public payable{
         require(msg.value==x*a.length);
         require(gasleft()>15000*a.length);
         for (uint256 b=0;b<a.length;b++) {
             payable(a[b]).send(x);
         }
     }
 }
