// contracts/GameItems.sol
// SPDX-License-Identifier: MIT
pragma solidity >=0.6.12;



import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/v4.3.2/contracts/token/ERC1155/ERC1155.sol";
import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/v4.3.2/contracts/token/ERC20/IERC20.sol";
import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/v4.3.2/contracts/security/ReentrancyGuard.sol"; 
import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/v4.3.2/contracts/access/Ownable.sol";
import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/v4.3.2/contracts/token/ERC1155/utils/ERC1155Holder.sol";
import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/v4.3.2/contracts/utils/math/SafeMath.sol"; 
import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/v4.3.2/contracts/token/ERC1155/extensions/ERC1155Supply.sol"; 
import "./interfaces/ILendingPool.sol";
import "./interfaces/ILendingPoolAddressesProvider.sol";
import "./interfaces/IWETHGateway.sol";  



pragma experimental ABIEncoderV2; 

contract DarkEther is ERC1155Supply, ReentrancyGuard, Ownable {
    using SafeMath for uint256;


    uint256 public constant MINT_LIMIT = 500000; 
    uint256 public constant MINIMUM_MINTS_FOR_CLAIM = 10; // users must mint atleast 10 times to qualify for rewards 
    uint256 public constant MINTS_SINCE_CLAIM_THRESHOLD = 100; // # of mints needed since last claim for rewards window to be available
    uint256 public constant mintPrice = 0.004 ether; 
    uint256 public constant TRANSFER_TO_TREASURY_MINT_THRESHOLD = MINT_LIMIT/5; 
    uint public mintCounter; // tracks total number of mints
    uint256 public newMintsSinceClaim; // tracks mints since last claim. reset for every claim. claims can be made when this value == MINTS_SINCE_CLAIM_THRESHOLD
    uint private randNonce; 
    bool private isRewardAvailable; // window for pre mint limit reward claims
    bool private leftOverRewardsChecked; // any pre mint rewards not claimed after mint period
    address  aWETH = 0x030bA81f1c18d280636F32af80b9AAd02Cf0854e; // aWETH address
    address treasuryAddress = 0x000fc975EE9D37FC1508DcD9E33A56613F0ee25A; // Dark Ether Treasury
    IWETHGateway wethGateway; 
    ILendingPool lendingPool;
    ILendingPoolAddressesProvider lendingPoolAddressesProvider; // AAVE Lending Pool Addresses Provider

    event EntityMinted(
        address indexed minter,
        uint256 indexed entityClass,
        uint256 indexed amount
    ); 


    event MintRewardClaimed(
        address indexed claimer,
        uint indexed amount
    ); 

    // base level organisms and elements
    string[] private defaultDarkEtherEntities = [
        "Element 0",
        "Class 1 Element",
        "Class 2 Element",
        "Class 3 Element",
        "Class 4 Element",
        "Class 5 Element",
        "Class 6 Element",
        "Class 7 Organism",
        "Class 8 Organism",
        "Class 9 Organism",
        "Organism 10"
    ];

    // default mint balances per class. array index == class
    uint256[] private initialMintClassBalances = [
            1,
            1,
            2,
            10,
            10,
            100,
            100,        
            5,
            2,
            1,
            1      
        ];

    // tracks # of times a sepcific class was minted
    mapping(uint256 => uint256) private mintsForClass;

    // tracks # of mints per address
    mapping(address => uint256) private addressMintCount;

    constructor(
    )  ERC1155("")
     {

        lendingPoolAddressesProvider = ILendingPoolAddressesProvider(0xB53C1a33016B2DC2fF3653530bfF1848a515c8c5);
        wethGateway = IWETHGateway(0xcc9a0B7c43DC2a5F023Bb9b738E45B0Ef6B06E04);
        randNonce = 0;
        mintCounter = 0;
        newMintsSinceClaim = 0;
        isRewardAvailable = false;
        leftOverRewardsChecked = false;
    }

    function requestMint() public payable nonReentrant
    {
        require(mintCounter < MINT_LIMIT , "Dark Ether minting finished!");
        require(mintPrice <= msg.value, "Must send 0.004 ether.");
        mintRandomEntity();
    }

    function getDefaultName(uint256 tokenId) public view returns (string memory) {
        require(isInRange(tokenId) == true , "Invalid class. Must be between 0 and 10 inclusive.");
        return defaultDarkEtherEntities[tokenId];
    }

    function getDefaultNames() public view returns (string[] memory) {
        return defaultDarkEtherEntities;
    }

    // get number of times an entity was minted for a particular class
    function getMintsForClass(uint256 darkEthClass) public view returns (uint256) {
        require(isInRange(darkEthClass) == true , "Invalid class. Must be between 0 and 10 inclusive.");
        return mintsForClass[darkEthClass];
    }

    // get number of times an extenal party mints an entity
    function getMintsForAddress(address minterAddress) public view returns (uint256) {
        return addressMintCount[minterAddress];
    }

    function hasPreMintLeftoverRewards() internal returns (bool) {
        // only checking this once
        if(leftOverRewardsChecked == true){
            return false;
        }
        // unclaimed rewards after mint window
        if(mintCounter == MINT_LIMIT && address(this).balance > 0) {
            leftOverRewardsChecked = true;
            return true;
        }
        return false;
    } 

    function claimMintReward() public nonReentrant 
    {
        require(getMintsForAddress(msg.sender) >= MINIMUM_MINTS_FOR_CLAIM, "You must mint at least 5 times to qualify for reward.");
        if(mintCounter == MINT_LIMIT) {
            require(hasPreMintLeftoverRewards() == true, "There are no more rewards available.");
            doRewardClaim();
        } else {
            require(isRewardAvailable == true, "Transaction threshold not met. Rewards currently unavailable.");
            doRewardClaim();
        }
    }

    function transferToTreasury() public onlyOwner {
        require(mintCounter >= TRANSFER_TO_TREASURY_MINT_THRESHOLD, "Threshold not met. Transfer cannot be completed at this time.");
        require(IERC20(aWETH).balanceOf(address(this)) > 0, "Contract has empty aWeth balance.");

        IERC20(aWETH).approve(treasuryAddress, IERC20(aWETH).balanceOf(address(this)));
        IERC20(aWETH).transfer(treasuryAddress, IERC20(aWETH).balanceOf(address(this))); 
    }

    function getRewardAvailability() public view returns (bool) {
        return isRewardAvailable;
    }

    function getAWethBalance() public view returns (uint256) {
        return IERC20(aWETH).balanceOf(address(this));
    }

    function getClass(uint256[] memory darkEtherId) private pure returns (uint256) {
         uint256 onesCount = 0;
         for (uint256 i = 0; i < darkEtherId.length; i++) {
            if(darkEtherId[i] == 1) {
                onesCount = onesCount + 1;
            }
        }
        return onesCount;
    }

    function generateDarkEtherId(uint256 randomValue) internal pure returns (uint256[] memory darkEtherId) {
        darkEtherId = expand(randomValue, 10);
        for (uint256 i = 0; i < 10; i++) {
            darkEtherId[i] = (uint256(keccak256(abi.encode(randomValue, i))) % 2);
        }
        return darkEtherId;
    }

    function expand(uint256 randomValue, uint256 n) internal pure returns (uint256[] memory expandedValues) {
        expandedValues = new uint256[](n);
        for (uint256 i = 0; i < n; i++) {
            expandedValues[i] = (uint256(keccak256(abi.encode(randomValue, i))) % 2);
        }
        return expandedValues;
    }

    function isInRange(uint256 value) internal pure returns (bool) {
        if(value < 0 || value > 10) {
          return false;
        }
        return true;
    }


    function doRewardClaim() internal 
    {
        require(address(this).balance > 0 , "Contract balance is empty.");
        // ether to be deposited in exchange for aWeth
        // this value grows and resets after a claim is made
        uint accruedFees =  address(this).balance;
        // 50% of accrued fees
        uint reward = (accruedFees/10).mul(5); 
        // deposit all accrued fees in eth to AAVE WETH lending pool in exchange for aWETH
        // aWETH obtained is at a 1:1 ratio to eth/Weth deposited
        address wethLendingPool = lendingPoolAddressesProvider.getLendingPool();
        wethGateway.depositETH{value: accruedFees }(wethLendingPool, address(this), 0);
        // send reward in aWETH to user
        IERC20(aWETH).approve(msg.sender, reward);
        IERC20(aWETH).transfer(msg.sender, reward);
        // reset new mints since last claim
        newMintsSinceClaim = 0;
        isRewardAvailable = false;
        emit MintRewardClaimed(msg.sender, reward);
    }


    function mintRandomEntity() internal  {
        uint256[] memory darkEther = generateDarkEtherId(rand());
        uint256 class = getClass(darkEther);
        uint256 mintBalance = initialMintClassBalances[class];
        _mint(msg.sender, class, mintBalance, "");
        mintsForClass[class] = mintsForClass[class] + 1;
        addressMintCount[msg.sender] = addressMintCount[msg.sender] + 1;
        mintCounter = mintCounter + 1;
        newMintsSinceClaim  = newMintsSinceClaim + 1;
        if(newMintsSinceClaim >= MINTS_SINCE_CLAIM_THRESHOLD) {
            isRewardAvailable = true;
        }
        emit EntityMinted(msg.sender, class, mintBalance); 
    }

    function rand() internal returns (uint256) 
    {
        randNonce++;  
        return uint256(keccak256(abi.encodePacked(block.timestamp, msg.sender,randNonce)));
    }
}

