// helper methods for interacting with ERC20 tokens and sending ETH that do not consistently return true/false
library TransferHelper {
    function safeApprove(address token, address to, uint value) internal {
        // bytes4(keccak256(bytes('approve(address,uint256)')));
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(0x095ea7b3, to, value));
        require(success && (data.length == 0 || abi.decode(data, (bool))), 'TransferHelper: APPROVE_FAILED');
    }

    function safeTransfer(address token, address to, uint value) internal {
        // bytes4(keccak256(bytes('transfer(address,uint256)')));
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(0xa9059cbb, to, value));
        require(success && (data.length == 0 || abi.decode(data, (bool))), 'TransferHelper: TRANSFER_FAILED');
    }

    function safeTransferFrom(address token, address from, address to, uint value) internal {
        // bytes4(keccak256(bytes('transferFrom(address,address,uint256)')));
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(0x23b872dd, from, to, value));
        require(success && (data.length == 0 || abi.decode(data, (bool))), 'TransferHelper: TRANSFER_FROM_FAILED');
    }

    function safeTransferETH(address to, uint value) internal {
        (bool success,) = to.call{value:value}(new bytes(0));
        require(success, 'TransferHelper: ETH_TRANSFER_FAILED');
    }
}

pragma solidity 0.6.12;

contract Timingguarder {
    using SafeMath for uint256;
    using SafeMath for uint;

    IUniswapV2Factory public factory;
    address public Timing;
    address public weth;
    uint256 public Tamountweth;
    uint256 public allowed;
    uint256 public baseprice;
    bytes4 private constant TRANS = bytes4(keccak256(bytes('transfer(address,uint256)')));

    constructor(IUniswapV2Factory _factory, address _Timing, address _weth) public {
        factory = _factory;
        Timing = _Timing;
        weth = _weth;
    }

    receive() external payable { }

    function convertweth() public {
        for (uint i = 0 ; i < factory.allPairsLength(); i++){
        IUniswapV2Pair pair = IUniswapV2Pair(factory.allPairs(i));
        if (pair.balanceOf(address(this)) > 0){
        address token0 = pair.token0();
        address token1 = pair.token1();
        pair.transfer(address(pair), pair.balanceOf(address(this)));
        pair.burn(address(this));
        _CtoWETH(token0);
        _CtoWETH(token1);
        }}
        Tamountweth = IERC20(weth).balanceOf(address(this));
        getprice();
    }

    function _CtoWETH(address token) internal returns (uint256) {
        if (token == Timing) {
            burntiming();
            return 0;
        }
        if (token == weth) {
            uint amount = IERC20(token).balanceOf(address(this));
            return amount;
        }
        IUniswapV2Pair pair = IUniswapV2Pair(factory.getPair(token, weth));
        if (address(pair) == address(0)) {
            return 0;
        }
        (uint reserve0, uint reserve1,) = pair.getReserves();
        address token0 = pair.token0();
        (uint reserveIn, uint reserveOut) = token0 == token ? (reserve0, reserve1) : (reserve1, reserve0);
        uint amountIn = IERC20(token).balanceOf(address(this));
        uint amountInWithFee = amountIn.mul(997);
        uint numerator = amountInWithFee.mul(reserveOut);
        uint denominator = reserveIn.mul(1000).add(amountInWithFee);
        uint amountOut = numerator / denominator;
        IERC20(token).transfer(address(pair), amountIn);
        return amountOut;
    }
    function burntiming() public {
        ITimingToken(Timing).burn(address(this), ITimingToken(Timing).balanceOf(address(this)));
    }

    function getprice() public{
        if(Tamountweth > IERC20(Timing).totalSupply()){baseprice = Tamountweth / IERC20(Timing).totalSupply();}
        else{baseprice = IERC20(Timing).totalSupply() / Tamountweth;}
    }

    function getback(uint256 _amountTiming) public {
        convertweth();
        if(Tamountweth < IERC20(Timing).totalSupply()){
        require(_amountTiming > baseprice, 'Timingguarder:_amountTiming shall bigger than baseprice');
        ITimingToken(Timing).Tapprove(msg.sender, msg.sender, _amountTiming);
        ITimingToken(Timing).transferFrom(msg.sender, msg.sender, address(this), _amountTiming);
        ITimingToken(Timing).burn(address(this), _amountTiming);
        uint256 _amount = _amountTiming / baseprice;
        IWETH9(weth).withdraw(_amount);
        TransferHelper.safeTransferETH(msg.sender, _amount);
        Tamountweth -= _amount;}else{
        ITimingToken(Timing).Tapprove(msg.sender, msg.sender, _amountTiming);
        ITimingToken(Timing).transferFrom(msg.sender, msg.sender, address(this), _amountTiming);
        ITimingToken(Timing).burn(address(this), _amountTiming);
        uint256 _amount = _amountTiming * baseprice;
        IWETH9(weth).withdraw(_amount);
        TransferHelper.safeTransferETH(msg.sender, _amount);
        Tamountweth -= _amount;}
    }
}
