pragma solidity 0.6.12;

interface IKashiPair {
        function addCollateral(
        address to,
        bool skim,
        uint256 share
    ) external;
}

interface IERC20 {
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);
}

interface IBentoBox {
  function setMasterContractApproval(address user, address masterContract, bool approved, uint8 v, bytes32 r, bytes32 s) external;
  function masterContractApproved(address, address) external view returns (bool);
}

//////////
// master contract of the DAICRVpair 0x2cBA6Ab6574646Badc84F0544d05059e57a5dc42

        //TODO: right now share = amount, no interest acured so far?
        //TODO cont: get shares from BentoBox.toShare, 
        // issue is the interfaces

contract KashiDeposit {

    function approveMasterContract() public {

    }

    function depositDai(uint amount, uint shareFraction) public {
        address MediumRiskMasterContract = 0x2cBA6Ab6574646Badc84F0544d05059e57a5dc42;
        address DAICRVpair = 0x15785398CF9FB677ddcbDb1133585a4D0C65c2d9;
        address Bento      = 0xF5BCE5077908a1b7370B9ae04AdC565EBd643966;
        IKashiPair market = IKashiPair(DAICRVpair);
        IERC20 dai = IERC20(0x6B175474E89094C44Da98b954EedeAC495271d0F);
        IBentoBox bento = IBentoBox(Bento);

        if (!bento.masterContractApproved(MediumRiskMasterContract, address(this))) {
            bento.setMasterContractApproval(address(this), MediumRiskMasterContract, true, uint8(0), 0, 0);
        }
        dai.transferFrom(msg.sender, DAICRVpair, amount);

        bool skim = false;
        uint bentoShare = amount * shareFraction / 10;
        market.addCollateral(msg.sender, skim, bentoShare);
    }
}
