interface ITornadoInstance {
  function deposit(address _tornado, bytes32 _commitment, bytes calldata _encryptedNote) external payable;
}

contract TornadoDeposit {
    ITornadoInstance constant proxy = ITornadoInstance(0x905b63Fff465B9fFBF41DeA908CEb12478ec7601);

    function batch(address _tornado, uint256 _amount, bytes32[] calldata _commitments) external payable {
        uint256 size = _commitments.length;
        for (uint256 i = 0; i < size; i++) {
            proxy.deposit{ value: _amount }(_tornado, _commitments[i], bytes(""));
        }
        msg.sender.transfer(address(this).balance);
    }
}
