pragma solidity ^0.7.0;

contract ListLaunchpad {

    address[] public _projects;
    address _owner;

    modifier onlyOwner {
        require(msg.sender == _owner);
        _;
    }

    constructor(address owner) {
        _owner = owner;  
    }

    function strConcat(string memory _a, string memory _b) internal pure returns (string memory _concatenatedString) {
        return strConcat(_a, _b, "", "", "");
    }

    function strConcat(string memory _a, string memory _b, string memory _c) internal pure returns (string memory _concatenatedString) {
        return strConcat(_a, _b, _c, "", "");
    }

    function strConcat(string memory _a, string memory _b, string memory _c, string memory _d) internal pure returns (string memory _concatenatedString) {
        return strConcat(_a, _b, _c, _d, "");
    }

    function strConcat(string memory _a, string memory _b, string memory _c, string memory _d, string memory _e) internal pure returns (string memory _concatenatedString) {
        bytes memory _ba = bytes(_a);
        bytes memory _bb = bytes(_b);
        bytes memory _bc = bytes(_c);
        bytes memory _bd = bytes(_d);
        bytes memory _be = bytes(_e);
        string memory abcde = new string(_ba.length + _bb.length + _bc.length + _bd.length + _be.length);
        bytes memory babcde = bytes(abcde);
        uint k = 0;
        uint i = 0;
        for (i = 0; i < _ba.length; i++) {
            babcde[k++] = _ba[i];
        }
        for (i = 0; i < _bb.length; i++) {
            babcde[k++] = _bb[i];
        }
        for (i = 0; i < _bc.length; i++) {
            babcde[k++] = _bc[i];
        }
        for (i = 0; i < _bd.length; i++) {
            babcde[k++] = _bd[i];
        }
        for (i = 0; i < _be.length; i++) {
            babcde[k++] = _be[i];
        }
        return string(babcde);
    }    

    function addressToString(address _addr) private pure returns(string memory) {
        bytes32 value = bytes32(uint256(_addr));
        bytes memory alphabet = "0123456789abcdef";

        bytes memory str = new bytes(51);
        str[0] = "0";
        str[1] = "x";
        for (uint i = 0; i < 20; i++) {
            str[2+i*2] = alphabet[uint(uint8(value[i + 12] >> 4))];
            str[3+i*2] = alphabet[uint(uint8(value[i + 12] & 0x0f))];
        }
        return string(str);
    }  



    function addNewProject(address newProject) external onlyOwner {
        _projects.push(newProject);
    }

    function deleteProject(uint index) external onlyOwner {
        require(index < _projects.length, 'Wrong index');
        delete _projects[index];   
    }

    function getIdProject(address project) public view returns (uint id) {
        for(uint i = 0; i < _projects.length; i++) {
            if(project == _projects[i]) {
                id = i;
            }
        }
    }

    function getAllProjects() public view returns (string memory projects) {
        projects = '';
        for(uint i = 0; i < _projects.length; i++) {
            if(_projects[i] != address(0)) {
                projects = strConcat(projects, "_", addressToString(_projects[i]));   
            }
        }
    }

}
