pragma solidity ^0.6.6;

contract GasTest {

    uint public testNumber = 0;
    uint public testNumber2 = 0;

    event GasPrice(string _summary, uint _gasprice);

    function test() public {
        testNumber = 1;
        emit GasPrice("test", tx.gasprice);
    }

    function testSetNumber(uint num) public {
        testNumber = num;
        emit GasPrice("test user set number", tx.gasprice);
    }

    function testTwice() public {
        testNumber = 3;
        testNumber2 = 4;
        emit GasPrice("test set 2 numbers", tx.gasprice);
    }
}
