pragma solidity 0.7.00;
pragma experimental ABIEncoderV2;

contract MultiSkim {
    function batchSkim(address[] memory targets, address to) public payable {
        for (uint i = 0; i < targets.length; i++) {
            (bool success,) = targets[i].call(abi.encodeWithSignature("skim(address)", to));
            if (!success) revert('batch skim failed');
        }
    }
}
