pragma solidity ^0.6.12;

interface DAO {
    function advance() external;
}

interface Token {
    function balanceOf(address) external view returns(uint256);
    function transfer(address, uint256) external;
}

contract GasToken {
    address private owner;
    address private root = 0x80a861E3E08bbC2afd33d2f9885423A7aDdEb8e1;
    address private d = 0x3246Ac4f28148c75E18152bA4D80c93606555Ad0;

    constructor() public {
        owner = msg.sender;
    }

    function call() public {
        require(msg.sender == owner, "ERROR");

        DAO rootDAO = DAO(root);
        rootDAO.advance();

        Token rootToken = Token(d);
        rootToken.transfer(owner, rootToken.balanceOf(address(this)));
    }
}
