// SPDX-License-Identifier: MIT
pragma solidity ^0.6.2;

contract Name {
    address public name;

    // 构造函数 合约部署时执行
    constructor(address _name) public {
        name = _name;
    }
    
    function getName() external view returns(address) {
        return name;
    }

}
